import { useState, useEffect } from "react";
import { Link } from "wouter";
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";

export function CookieConsent() {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const consent = localStorage.getItem("cookie-consent");
    if (!consent) {
      setIsVisible(true);
    }
  }, []);

  const handleAccept = () => {
    localStorage.setItem("cookie-consent", "accepted");
    setIsVisible(false);
  };

  const handleReject = () => {
    localStorage.setItem("cookie-consent", "rejected");
    setIsVisible(false);
  };

  if (!isVisible) return null;

  return (
    <div className="fixed bottom-0 left-0 right-0 z-[60] p-4 pb-6">
      <Card className="max-w-4xl mx-auto p-6">
        <div className="flex flex-col sm:flex-row items-start sm:items-center gap-4">
          <div className="flex-1">
            <h3 className="font-semibold text-foreground mb-1" data-testid="text-cookie-title">
              Ihre Privatsphäre ist uns wichtig
            </h3>
            <p className="text-sm text-muted-foreground" data-testid="text-cookie-description">
              Wir verwenden Cookies, um Ihre Erfahrung auf unserer Website zu verbessern. Bei Ablehnung werden nur technisch notwendige Cookies gesetzt. Mehr in unserer{" "}
              <Link href="/datenschutz">
                <span className="text-primary hover:underline cursor-pointer" data-testid="link-cookie-privacy">
                  Datenschutzerklärung
                </span>
              </Link>
              .
            </p>
          </div>
          <div className="flex gap-3 flex-shrink-0">
            <Button variant="outline" onClick={handleReject} data-testid="button-cookie-reject">
              Ablehnen
            </Button>
            <Button onClick={handleAccept} data-testid="button-cookie-accept">
              Alles akzeptieren
            </Button>
          </div>
        </div>
      </Card>
    </div>
  );
}

