import { useState, useRef, useEffect } from "react";
import { Link, useLocation } from "wouter";
import { Menu, X, ChevronDown, Zap, ClipboardCheck, Building, BookOpen, Leaf, ArrowRight } from "lucide-react";
import { Button } from "@/components/ui/button";
import palvarLeaf from "@assets/generated_images/transparent_green_leaf_icon.png";

export function Header() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [activeNav, setActiveNav] = useState<string | null>(null);
  const [location] = useLocation();
  const headerRef = useRef<HTMLDivElement>(null);

  const navItems = [
    { 
      label: "Energieberatung", 
      icon: Zap,
      href: "/energieberatung",
      description: "Professionelle Energieberatung für Ihr Gebäude",
      items: [
        { href: "/energieberatung", label: "Übersicht & Pakete", description: "Alle Leistungen im Überblick" },
        { href: "/energieberatung/bestandsaufnahme", label: "Bestandsaufnahme & Gebäudebewertung", description: "Analyse Ihres Gebäudezustands" },
        { href: "/energieberatung/energieausweis", label: "Energieausweis", description: "Bedarfs- und Verbrauchsausweise nach GEG" },
        { href: "/energieberatung/isfp", label: "Individueller Sanierungsfahrplan (iSFP)", description: "Ihr persönlicher Sanierungsplan" },
        { href: "/energieberatung/foerdermittel", label: "Fördermittelberatung", description: "Maximale Förderung sichern" },
      ]
    },
    { 
      label: "Projektmanagement", 
      icon: ClipboardCheck,
      href: "/projektmanagement",
      description: "Professionelle Projektsteuerung für Sanierungen",
      items: [
        { href: "/projektmanagement", label: "Übersicht & Leistungen", description: "Unsere Projektmanagement-Services" },
        { href: "/projektmanagement/bauherrenvertretung", label: "Bauherrenvertretung", description: "Ihre Interessen vertreten" },
        { href: "/projektmanagement/qualitaetssicherung", label: "Baubegleitung & Qualitätssicherung", description: "Förderfähige Baubegleitung" },
        { href: "/projektmanagement/heizungsprojekte", label: "Heizungsprojekte", description: "Wärmepumpen & Heizungstausch" },
        { href: "/projektmanagement/sanierungsmanagement", label: "Sanierungsmanagement", description: "Komplexe Sanierungen steuern" },
      ]
    },
    { 
      label: "Gebäudeservices", 
      icon: Building,
      href: "/gebaeudeservices",
      description: "Ganzheitliche Gebäudebetreuung",
      items: [
        { href: "/gebaeudeservices", label: "Übersicht & Leistungen", description: "Alle Services im Überblick" },
        { href: "/gebaeudeservices/standardleistungen", label: "Standardleistungen", description: "Reinigung, Hausmeister, Winterdienst" },
        { href: "/gebaeudeservices/gebaeuedebetrieb", label: "Energetischer Gebäudebetrieb", description: "Effizienter Betrieb Ihrer Immobilie" },
        { href: "/gebaeudeservices/energiecontrolling", label: "Energiecontrolling & Monitoring", description: "Verbräuche im Blick" },
        { href: "/gebaeudeservices/bestandsentwicklung", label: "Strategische Bestandsentwicklung", description: "Langfristige Wertsteigerung" },
        { href: "/gebaeudeservices/verwaltungsberatung", label: "Eigentümer- & Verwaltungsberatung", description: "Beratung für Eigentümer" },
      ]
    },
    { 
      label: "Wissen", 
      icon: BookOpen,
      href: "/wissen",
      description: "Wissenswertes rund um Energieeffizienz",
      items: [
        { href: "/wissen", label: "Alle Wissensartikel", description: "Komplette Wissenssammlung" },
      ],
      sections: [
        {
          title: "Gesetz & Förderungen",
          items: [
            { href: "/wissen/geg", label: "Gebäudeenergiegesetz (GEG)", description: "Aktuelle Anforderungen" },
            { href: "/wissen/sanierungspflicht", label: "Sanierungspflichten", description: "Was bei Eigentümerwechsel gilt" },
          ]
        },
        {
          title: "Bauteile & Praxis-Tools",
          items: [
            { href: "/bauteile", label: "Bauteile & Förderung", description: "Komponenten im Detail" },
            { href: "/rechner", label: "Wirtschaftlichkeitsrechner", description: "Kosten & Ersparnis berechnen" },
          ]
        },
        {
          title: "Praxis & Hilfestellung",
          items: [
            { href: "/wissen/lexikon", label: "Bau-Lexikon", description: "Fachbegriffe einfach erklärt" },
            { href: "/checkliste-kaeufer", label: "Käufer-Checkliste", description: "Vor dem Immobilienkauf" },
            { href: "/faq", label: "Häufige Fragen (FAQ)", description: "Antworten auf Ihre Fragen" },
          ]
        }
      ]
    }
  ];

  const energieberatungPages = ["/energieberatung/isfp", "/energieberatung/foerdermittel", "/energieberatung/energieausweis", "/energieberatung/heizlastberechnung", "/energieberatung/bestandsaufnahme"];

  const isActive = (item: typeof navItems[0]) => {
    if (location === item.href) return true;
    if (location.startsWith(item.href + "/")) return true;
    if (item.label === "Energieberatung" && energieberatungPages.includes(location)) return true;
    if (item.items.some(sub => location === sub.href)) return true;
    if (item.sections) {
      return item.sections.some(section => section.items.some(sub => location === sub.href));
    }
    return false;
  };

  const handleNavClick = (label: string) => {
    if (activeNav === label) {
      setActiveNav(null);
    } else {
      setActiveNav(label);
    }
  };

  const handleLinkClick = () => {
    setActiveNav(null);
    setIsMenuOpen(false);
  };

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (headerRef.current && !headerRef.current.contains(event.target as Node)) {
        setActiveNav(null);
      }
    };

    const handleEscape = (event: KeyboardEvent) => {
      if (event.key === "Escape") {
        setActiveNav(null);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    document.addEventListener("keydown", handleEscape);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
      document.removeEventListener("keydown", handleEscape);
    };
  }, []);

  useEffect(() => {
    setActiveNav(null);
  }, [location]);

  // Disable body scroll when mobile menu is open
  useEffect(() => {
    if (isMenuOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = '';
    }
    return () => {
      document.body.style.overflow = '';
    };
  }, [isMenuOpen]);

  const activeItem = navItems.find(item => item.label === activeNav);

  return (
    <header 
      ref={headerRef}
      className={`fixed top-0 left-0 right-0 z-50 bg-background/95 backdrop-blur-sm border-b border-border ${activeNav ? 'rounded-b-none' : 'lg:rounded-b-xl'}`}
    >
      <div className="w-full max-w-[1920px] mx-auto px-4 sm:px-6 lg:px-8 2xl:px-12">
        <div className="flex items-center justify-between h-14">
          <Link href="/">
            <div 
              className="flex items-center gap-2 cursor-pointer"
              data-testid="link-logo"
            >
              <img 
                src={palvarLeaf} 
                alt="PALVAR Logo" 
                className="w-8 h-8 object-contain"
              />
              <span 
                className="text-lg sm:text-xl font-semibold tracking-wide uppercase bg-gradient-to-r from-foreground via-foreground to-primary bg-clip-text text-transparent"
                style={{ backgroundSize: '200% 100%', backgroundPosition: '30% 0' }}
              >
                PALVAR
              </span>
            </div>
          </Link>

          {/* Desktop Navigation with Mega Menu */}
          <nav className="hidden lg:flex items-center gap-1 flex-1 justify-start ml-6 flex-wrap">
            {navItems.map((item) => (
              <button
                key={item.label}
                onClick={() => handleNavClick(item.label)}
                className={`flex items-center gap-1.5 px-3 py-1.5 text-sm font-medium rounded-md cursor-pointer transition-colors ${
                  activeNav === item.label
                    ? "text-primary bg-accent"
                    : isActive(item)
                    ? "text-primary bg-accent/50"
                    : "text-muted-foreground hover:text-foreground hover:bg-accent/50"
                }`}
                data-testid={`dropdown-${item.label.toLowerCase().replace(" ", "-")}`}
              >
                <item.icon className="w-4 h-4" />
                {item.label}
                <ChevronDown className={`h-3 w-3 transition-transform duration-200 ${activeNav === item.label ? 'rotate-180' : ''}`} />
              </button>
            ))}
          </nav>

          {/* Right-aligned CTA */}
          <div className="hidden lg:block">
            <Link href="/anfrage">
              <Button className="bg-copper text-white border-copper px-6" data-testid="button-booking-header">
                Beratung anfordern
              </Button>
            </Link>
          </div>

          <Button
            variant="ghost"
            size="icon"
            className="lg:hidden"
            onClick={() => setIsMenuOpen(!isMenuOpen)}
            data-testid="button-mobile-menu"
          >
            {isMenuOpen ? <X className="h-5 w-5" /> : <Menu className="h-5 w-5" />}
          </Button>
        </div>

        {/* Mega Menu Panel - Desktop */}
        {activeNav && activeItem && (
          <div className="hidden lg:block border-t border-border">
            <div className="py-6">
              <div className="flex gap-8">
                {/* Left: Category Info */}
                <div className="w-64 flex-shrink-0">
                  <div className="flex items-center gap-2 mb-3">
                    <activeItem.icon className="w-5 h-5 text-primary" />
                    <h3 className="text-lg font-semibold text-foreground">{activeItem.label}</h3>
                  </div>
                  <p className="text-sm text-muted-foreground mb-4">{activeItem.description}</p>
                  <Link href={activeItem.href} onClick={handleLinkClick}>
                    <Button variant="outline" size="sm" className="gap-2" data-testid={`button-overview-${activeItem.label.toLowerCase()}`}>
                      Zur Übersicht
                      <ArrowRight className="w-4 h-4" />
                    </Button>
                  </Link>
                </div>

                {/* Right: Links Grid */}
                <div className="flex-1">
                  {activeItem.sections ? (
                    <div className="grid grid-cols-3 gap-6">
                      {activeItem.sections.map((section) => (
                        <div key={section.title}>
                          <h4 className="text-xs font-semibold text-muted-foreground uppercase tracking-wider mb-3">
                            {section.title}
                          </h4>
                          <div className="space-y-1">
                            {section.items.map((subItem) => (
                              <Link key={subItem.href} href={subItem.href} onClick={handleLinkClick}>
                                <div 
                                  className="group block px-3 py-2.5 rounded-md hover-elevate cursor-pointer"
                                  data-testid={`link-${subItem.label.toLowerCase().replace(/[^a-z0-9]/g, '-')}`}
                                >
                                  <span className="block text-sm font-semibold text-foreground group-hover:text-primary transition-colors">
                                    {subItem.label}
                                  </span>
                                  {'description' in subItem && (
                                    <span className="block text-xs text-muted-foreground mt-0.5">
                                      {subItem.description}
                                    </span>
                                  )}
                                </div>
                              </Link>
                            ))}
                          </div>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <div className="space-y-1">
                      {/* First item as header */}
                      {activeItem.items.length > 0 && (
                        <h4 className="text-xs font-semibold text-muted-foreground uppercase tracking-wider mb-3">
                          {activeItem.items[0].label}
                        </h4>
                      )}
                      {/* Remaining items in grid */}
                      <div className="grid grid-cols-2 gap-4">
                        {activeItem.items.slice(1).map((subItem) => (
                          <Link key={subItem.href} href={subItem.href} onClick={handleLinkClick}>
                            <div 
                              className="group block px-3 py-2.5 rounded-md hover-elevate cursor-pointer"
                              data-testid={`link-${subItem.label.toLowerCase().replace(/[^a-z0-9]/g, '-')}`}
                            >
                              <span className="block text-sm font-semibold text-foreground group-hover:text-primary transition-colors">
                                {subItem.label}
                              </span>
                              {'description' in subItem && (
                                <span className="block text-xs text-muted-foreground mt-0.5">
                                  {subItem.description}
                                </span>
                              )}
                            </div>
                          </Link>
                        ))}
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Mobile Menu - Simplified: Only 4 main categories */}
        {isMenuOpen && (
          <nav className="lg:hidden py-4 border-t border-border">
            <div className="flex flex-col gap-2 px-4">
              {navItems.map((item) => (
                <Link key={item.label} href={item.href}>
                  <div
                    className={`flex items-center gap-3 px-4 py-4 rounded-xl cursor-pointer transition-all duration-200 ${
                      isActive(item)
                        ? "bg-primary text-primary-foreground shadow-md"
                        : "bg-accent/50 text-foreground hover-elevate"
                    }`}
                    onClick={() => setIsMenuOpen(false)}
                    data-testid={`mobile-link-${item.label.toLowerCase().replace(" ", "-")}`}
                  >
                    <div className={`w-10 h-10 rounded-lg flex items-center justify-center ${
                      isActive(item) ? "bg-white/20" : "bg-primary/10"
                    }`}>
                      <item.icon className={`w-5 h-5 ${isActive(item) ? "text-white" : "text-primary"}`} />
                    </div>
                    <div>
                      <span className="block text-base font-semibold">{item.label}</span>
                      <span className={`block text-xs ${isActive(item) ? "text-white/70" : "text-muted-foreground"}`}>
                        {item.description}
                      </span>
                    </div>
                    <ArrowRight className={`w-4 h-4 ml-auto ${isActive(item) ? "text-white/70" : "text-muted-foreground"}`} />
                  </div>
                </Link>
              ))}

              <div className="pt-3 mt-2 border-t border-border">
                <Link href="/anfrage">
                  <Button className="w-full bg-copper text-white border-copper py-5 text-base" onClick={() => setIsMenuOpen(false)} data-testid="button-booking-mobile">
                    Beratung anfordern
                    <ArrowRight className="ml-2 w-4 h-4" />
                  </Button>
                </Link>
              </div>
            </div>
          </nav>
        )}
      </div>

      {/* Bottom border radius when mega menu is open */}
      {activeNav && (
        <div className="absolute bottom-0 left-0 right-0 h-3 bg-background/95 backdrop-blur-sm -mb-3 rounded-b-xl border-b border-l border-r border-border" />
      )}
    </header>
  );
}

