    subtitle: "Der Sanierungsfahrplan",
    price: "1.700 €",
    finalPrice: "inkl. iSFP & Heizlast",
    color: "bg-primary",
    highlight: true,
    features: ["Vollständige Gebäudeanalyse", "5% iSFP-Bonus", "Förderstrategie"]
  },
  {
    id: "Leuchtturm",
