    finalPrice: "inkl. Baubegleitung",
    color: "bg-emerald-700",
    features: ["Alles aus Navigator", "Baubegleitung", "Förderabwicklung"]
  }
];

const serviceCategories = [
  { id: "voranalyse", label: "Voranalyse", icon: Search, color: "text-amber-600 bg-amber-100 dark:bg-amber-900/30" },
  { id: "energieberatung", label: "Energieberatung", icon: Zap, color: "text-emerald-600 bg-emerald-100 dark:bg-emerald-900/30" },
