];

const consultingServices = [
  { id: "Strategieberatung", name: "Strategieberatung", desc: "Langfristige Immobilienstrategie" },
  { id: "Portfolioanalyse", name: "Portfolioanalyse", desc: "Bewertung Ihres Immobilienbestands" },
  { id: "Dekarbonisierung", name: "Dekarbonisierungsstrategie", desc: "CO2-Reduktionsfahrplan" },
  { id: "Investitionsplanung", name: "Investitionsplanung", desc: "Budgetplanung für Sanierungen" }
];

export default function Booking() {
  const { toast } = useToast();
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState<string>("voranalyse");

  const form = useForm<BookingFormData>({
    resolver: zodResolver(bookingSchema),
    defaultValues: {
      service: "",
      preferredDate: "",
      preferredTime: "",
      buildingType: "",
      buildingYear: "",
      livingSpace: "",
      units: "",
      heatingType: "",
      name: "",
      email: "",
      phone: "",
      street: "",
      postalCode: "",
      city: "",
      message: "",
      privacy: false,
    },
  });

  const mutation = useMutation({
    mutationFn: async (data: BookingFormData) => {
      const submitData = {
        ...data,
        consentGiven: data.privacy,
        consentTimestamp: data.privacy ? new Date().toISOString() : null,
      };
      return apiRequest("POST", "/api/anfrage", submitData);
    },
    onSuccess: () => {
      setIsSubmitted(true);
      toast({
        title: "Anfrage gesendet",
        description: "Vielen Dank! Wir melden uns in Kürze bei Ihnen.",
      });
    },
    onError: () => {
      toast({
        title: "Fehler",
        description: "Ihre Anfrage konnte nicht gesendet werden. Bitte versuchen Sie es erneut.",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: BookingFormData) => {
    mutation.mutate(data);
  };

  if (isSubmitted) {
    return (
      <>
        <SEO 
          title="Anfrage gesendet - PALVAR"
          description="Ihre Beratungsanfrage wurde erfolgreich gesendet."
          canonical="/anfrage"
        />
        <div className="min-h-screen flex items-center justify-center bg-gradient-to-b from-primary/5 to-background">
          <FadeIn>
            <Card className="max-w-md mx-4 text-center p-8 border-primary/20">
              <CardContent className="pt-0">
                <div className="w-20 h-20 bg-primary rounded-full flex items-center justify-center mx-auto mb-6">
                  <CheckCircle className="w-10 h-10 text-white" />
                </div>
                <h2 className="text-2xl font-bold font-heading text-foreground mb-4" data-testid="text-success-title">
                  Vielen Dank für Ihre Anfrage!
                </h2>
                <p className="text-muted-foreground mb-6" data-testid="text-success-description">
                  Wir haben Ihre Nachricht erhalten und werden uns innerhalb von 24 Stunden bei Ihnen melden.
                </p>
                <Link href="/">
                  <Button>Zurück zur Startseite</Button>
                </Link>
              </CardContent>
            </Card>
          </FadeIn>
        </div>
      </>
    );
  }

  return (
    <>
      <SEO 
        title="Beratung anfragen - PALVAR Energieberatung"
        description="Stellen Sie eine unverbindliche Anfrage für Ihre Energieberatung, Projektmanagement oder Gebäudeservices."
        canonical="/anfrage"
      />
      <div className="min-h-screen">
        {/* Hero Section with Background Image */}
        <section className="relative py-12 sm:py-16">
          <div 
            className="absolute inset-0 bg-cover bg-center"
            style={{
              backgroundImage: "url('https://images.unsplash.com/photo-1600880292203-757bb62b4baf?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&q=80&w=1920')"
            }}
          />
          <div className="absolute inset-0 bg-gradient-to-r from-emerald-950/95 via-emerald-950/60 to-emerald-950/30" />

          <div className="relative z-10 max-w-[1920px] mx-auto px-4 sm:px-6 lg:px-8">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 items-center">
              <FadeIn>
                <div>
                  <div className="inline-flex items-center gap-2 bg-copper/20 text-copper px-4 py-2 rounded-full mb-6 border border-copper/30">
                    <Calendar className="w-4 h-4" />
                    <span className="text-sm font-medium">Unverbindliche Anfrage</span>
                  </div>
                  <h1 className="text-3xl sm:text-4xl lg:text-5xl font-semibold font-heading text-white mb-6 leading-tight" data-testid="text-page-title">
                    Starten Sie Ihr Projekt
                  </h1>
                  <p className="text-lg text-white/80 mb-8 leading-relaxed" data-testid="text-page-description">
                    Füllen Sie das Formular aus und wir melden uns innerhalb von 24 Stunden bei Ihnen. 
                    Kostenlos und unverbindlich.
                  </p>
                  <div className="grid grid-cols-2 gap-4">
                    <div className="flex items-center gap-2 text-sm text-white/90">
                      <Clock className="w-4 h-4 text-copper" />
                      <span>24h Rückmeldung</span>
                    </div>
                    <div className="flex items-center gap-2 text-sm text-white/90">
                      <Shield className="w-4 h-4 text-copper" />
                      <span>100% kostenlos</span>
                    </div>
                    <div className="flex items-center gap-2 text-sm text-white/90">
                      <Award className="w-4 h-4 text-copper" />
                      <span>dena-gelistet</span>
                    </div>
                    <div className="flex items-center gap-2 text-sm text-white/90">
                      <CheckCircle className="w-4 h-4 text-copper" />
                      <span>Unverbindlich</span>
                    </div>
                  </div>
                </div>
              </FadeIn>

              <div className="hidden lg:block">
                <FadeIn delay={0.2}>
                  <div className="bg-white/10 backdrop-blur-md rounded-xl p-6 border border-white/20">
                    <h3 className="font-semibold text-white mb-4">Direkter Kontakt</h3>
                  <div className="space-y-4">
                    <a href="tel:+49123456789" className="flex items-center gap-3 p-3 rounded-lg bg-white/5 hover:bg-white/10 transition-colors">
                      <div className="w-10 h-10 bg-primary rounded-lg flex items-center justify-center">
                        <Phone className="w-5 h-5 text-white" />
                      </div>
                      <div>
                        <p className="text-sm text-white/60">Telefon</p>
                        <p className="font-semibold text-white">+49 (0) 123 456 789</p>
                      </div>
                    </a>
                    <a href="mailto:service@palvar.de" className="flex items-center gap-3 p-3 rounded-lg bg-white/5 hover:bg-white/10 transition-colors">
                      <div className="w-10 h-10 bg-copper rounded-lg flex items-center justify-center">
                        <Mail className="w-5 h-5 text-white" />
                      </div>
                      <div>
                        <p className="text-sm text-white/60">E-Mail</p>
                        <p className="font-semibold text-white">service@palvar.de</p>
                      </div>
                    </a>
                    </div>
                  </div>
                </FadeIn>
              </div>
            </div>
          </div>
        </section>

        {/* Form Section */}
        <section className="py-12 bg-background">
          <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-8">

                {/* Step 1: Service Selection */}
                <FadeInView>
                  <div className="bg-surface rounded-xl p-6 border border-border">
                    <div className="flex items-center gap-3 mb-6">
                      <div className="w-8 h-8 bg-primary text-white rounded-full flex items-center justify-center font-bold text-sm">1</div>
                      <h2 className="text-lg font-bold text-foreground">Leistung auswählen</h2>
                    </div>

                    {/* Category Tabs */}
                    <div className="flex flex-wrap gap-2 mb-6">
                      {serviceCategories.map((cat) => (
                        <button
                          key={cat.id}
                          type="button"
                          onClick={() => setSelectedCategory(cat.id)}
                          className={`flex items-center gap-2 px-4 py-2 rounded-lg text-sm font-medium transition-all ${
                            selectedCategory === cat.id 
                              ? cat.color
                              : 'bg-muted text-muted-foreground hover:bg-muted/80'
                          }`}
                          data-testid={`tab-${cat.id}`}
                        >
                          <cat.icon className="w-4 h-4" />
                          {cat.label}
                        </button>
                      ))}
                    </div>

                    {/* Voranalyse Packages */}
                    {selectedCategory === "voranalyse" && (
                      <div className="space-y-4">
                        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                          {voranalysePackages.map((pkg) => (
                            <div 
                              key={pkg.id}
                              onClick={() => form.setValue("service", pkg.id)}
                              className={`relative cursor-pointer rounded-xl p-5 transition-all border-2 ${
                                form.watch("service") === pkg.id 
                                  ? "border-amber-600 bg-amber-500/5 shadow-lg" 
                                  : "border-border hover:border-amber-600/50"
                              } ${pkg.highlight ? "ring-2 ring-amber-500/20" : ""}`}
                              data-testid={`card-package-${pkg.id}`}
                            >
                              {pkg.highlight && (
                                <Badge className="absolute -top-2 left-4 bg-amber-600 text-white text-xs">
                                  Empfohlen
                                </Badge>
                              )}
                              <div className={`w-10 h-10 ${pkg.color} rounded-lg flex items-center justify-center mb-3`}>
                                <Search className="w-5 h-5 text-white" />
                              </div>
                              <h4 className="font-bold text-foreground">{pkg.title}</h4>
                              <p className="text-xs text-muted-foreground mb-3">{pkg.subtitle}</p>
                              <p className="text-lg font-bold text-foreground">{pkg.price}</p>
                              <p className="text-sm text-amber-600 font-medium mb-3">{pkg.finalPrice}</p>
                              <ul className="space-y-1">
                                {pkg.features.map((f, i) => (
                                  <li key={i} className="flex items-center gap-2 text-xs text-muted-foreground">
                                    <Check className="w-3 h-3 text-amber-600 flex-shrink-0" />
                                    {f}
                                  </li>
                                ))}
                              </ul>
                              {form.watch("service") === pkg.id && (
                                <div className="absolute top-4 right-4 w-6 h-6 bg-amber-600 rounded-full flex items-center justify-center">
                                  <Check className="w-4 h-4 text-white" />
                                </div>
                              )}
                            </div>
                          ))}
                        </div>
                        <LegalDisclaimer variant="voranalyse" />
                      </div>
                    )}

                    {/* Energieberatung Packages */}
                    {selectedCategory === "energieberatung" && (
                      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                        {energieberatungPackages.map((pkg) => (
                          <div 
                            key={pkg.id}
                            onClick={() => form.setValue("service", pkg.id)}
                            className={`relative cursor-pointer rounded-xl p-5 transition-all border-2 ${
                              form.watch("service") === pkg.id 
                                ? "border-primary bg-primary/5 shadow-lg" 
                                : "border-border hover:border-primary/50"
                            } ${pkg.highlight ? "ring-2 ring-primary/20" : ""}`}
                            data-testid={`card-package-${pkg.id}`}
                          >
                            {pkg.highlight && (
                              <Badge className="absolute -top-2 left-4 bg-primary text-white text-xs">
                                Beliebt
                              </Badge>
                            )}
                            <div className={`w-10 h-10 ${pkg.color} rounded-lg flex items-center justify-center mb-3`}>
                              <Zap className="w-5 h-5 text-white" />
                            </div>
                            <h4 className="font-bold text-foreground">{pkg.title}</h4>
                            <p className="text-xs text-muted-foreground mb-3">{pkg.subtitle}</p>
                            <p className="text-lg font-bold text-foreground">{pkg.price}</p>
                            <p className="text-sm text-primary font-medium mb-3">{pkg.finalPrice}</p>
                            <ul className="space-y-1">
                              {pkg.features.map((f, i) => (
                                <li key={i} className="flex items-center gap-2 text-xs text-muted-foreground">
                                  <Check className="w-3 h-3 text-primary flex-shrink-0" />
                                  {f}
                                </li>
                              ))}
                            </ul>
                            {form.watch("service") === pkg.id && (
                              <div className="absolute top-4 right-4 w-6 h-6 bg-primary rounded-full flex items-center justify-center">
                                <Check className="w-4 h-4 text-white" />
                              </div>
                            )}
                          </div>
                        ))}
                      </div>
                    )}

                    {/* Projektmanagement */}
                    {selectedCategory === "projektmanagement" && (
                      <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                        {["Bauherrenvertretung", "Baubegleitung", "Heizungsprojekte", "Individuelles Projekt"].map((service) => (
                          <div 
                            key={service}
                            onClick={() => form.setValue("service", service)}
                            className={`cursor-pointer rounded-xl p-4 transition-all border-2 flex items-center gap-3 ${
                              form.watch("service") === service 
                                ? "border-primary bg-primary/5" 
                                : "border-border hover:border-primary/50"
                            }`}
                            data-testid={`card-package-${service}`}
                          >
                            <div className="w-10 h-10 bg-primary rounded-lg flex items-center justify-center">
                              <Settings className="w-5 h-5 text-white" />
                            </div>
                            <div className="flex-1">
                              <h4 className="font-semibold text-foreground text-sm">{service}</h4>
                              <p className="text-xs text-primary">Auf Anfrage</p>
                            </div>
                            {form.watch("service") === service && (
                              <div className="w-5 h-5 bg-primary rounded-full flex items-center justify-center">
                                <Check className="w-3 h-3 text-white" />
                              </div>
