import { Helmet } from "react-helmet-async";

interface SEOProps {
  title?: string;
  description?: string;
  canonical?: string;
  type?: "website" | "article" | "service";
  image?: string;
  noindex?: boolean;
  structuredData?: object | object[];
}

const defaultMeta = {
  siteName: "PALVAR",
  siteUrl: "https://palvar.de",
  defaultTitle: "PALVAR - Energieberatung, Projektmanagement & Gebäudeservices",
  defaultDescription: "Ihr Partner für energetische Optimierung, Sanierungsberatung und professionelles Immobilienmanagement. Fördermittelberatung, iSFP, Baubegleitung und Facility Management aus einer Hand.",
  defaultImage: "https://palvar.de/og-image.jpg",
};

export function SEO({ 
  title, 
  description, 
  canonical, 
  type = "website",
  image,
  noindex = false,
  structuredData
}: SEOProps) {
  const pageTitle = title 
    ? `${title} | ${defaultMeta.siteName}` 
    : defaultMeta.defaultTitle;
  const pageDescription = description || defaultMeta.defaultDescription;
  const pageUrl = canonical ? `${defaultMeta.siteUrl}${canonical}` : defaultMeta.siteUrl;
  const pageImage = image || defaultMeta.defaultImage;

  return (
    <Helmet>
      <title>{pageTitle}</title>
      <meta name="description" content={pageDescription} />
      <link rel="canonical" href={pageUrl} />

      {noindex && <meta name="robots" content="noindex, follow" />}

      <meta property="og:type" content={type} />
      <meta property="og:url" content={pageUrl} />
      <meta property="og:title" content={pageTitle} />
      <meta property="og:description" content={pageDescription} />
      <meta property="og:image" content={pageImage} />
      <meta property="og:site_name" content={defaultMeta.siteName} />
      <meta property="og:locale" content="de_DE" />

      <meta name="twitter:card" content="summary_large_image" />
      <meta name="twitter:url" content={pageUrl} />
      <meta name="twitter:title" content={pageTitle} />
      <meta name="twitter:description" content={pageDescription} />
      <meta name="twitter:image" content={pageImage} />

      {structuredData && (
        <script type="application/ld+json">
          {JSON.stringify(Array.isArray(structuredData) ? structuredData : structuredData)}
        </script>
      )}
    </Helmet>
  );
}

