      {
        "@type": "Offer",
        "itemOffered": {
          "@type": "Service",
          "name": "Property Services",
          "description": "Facility Management, Property Management und Asset Management"
        }
      }
    ]
  },
  "priceRange": "€€",
  "telephone": "+49-123-456789"
};

export function createBreadcrumbSchema(items: { name: string; url: string }[]) {
  return {
    "@context": "https://schema.org",
    "@type": "BreadcrumbList",
    "itemListElement": items.map((item, index) => ({
      "@type": "ListItem",
      "position": index + 1,
      "name": item.name,
      "item": `https://palvar.de${item.url}`
    }))
  };
}

export function createFAQSchema(faqs: { question: string; answer: string }[]) {
  return {
    "@context": "https://schema.org",
    "@type": "FAQPage",
    "mainEntity": faqs.map(faq => ({
      "@type": "Question",
      "name": faq.question,
      "acceptedAnswer": {
        "@type": "Answer",
        "text": faq.answer
      }
    }))
  };
}

export function createServiceSchema(service: {
  name: string;
  description: string;
  url: string;
  provider?: string;
}) {
  return {
    "@context": "https://schema.org",
    "@type": "Service",
    "name": service.name,
    "description": service.description,
    "url": `https://palvar.de${service.url}`,
    "provider": {
      "@type": "Organization",
      "name": service.provider || "PALVAR"
    },
    "areaServed": {
      "@type": "Country",
      "name": "Germany"
    }
  };
}

