function HeizungsRechner() {
  const [heizart, setHeizart] = useState("gas");
  const [verbrauch, setVerbrauch] = useState("20000");
  const [waermepumpenTyp, setWaermepumpenTyp] = useState("luft");
  const [neueHeizung, setNeueHeizung] = useState("waermepumpe");
  const [wohneinheiten, setWohneinheiten] = useState("1");
  const [klimaBonus, setKlimaBonus] = useState(true);
  const [einkommensBonus, setEinkommensBonus] = useState(false);
  const [effizienzBonus, setEffizienzBonus] = useState(false);

  const ergebnis = useMemo(() => {
    const verbrauchKwh = parseFloat(verbrauch) || 20000;
    const gasPreis = 0.12;
    const oelPreis = 0.11;
    const stromPreis = 0.30;
    const pelletPreis = 0.065;

    const aktuellerPreis = heizart === "gas" ? gasPreis : oelPreis;
    const aktuelleKosten = verbrauchKwh * aktuellerPreis;

    let cop = 1;
    let neueKosten = 0;
    let investition = 0;

    if (neueHeizung === "waermepumpe") {
      cop = waermepumpenTyp === "luft" ? 3.5 : waermepumpenTyp === "sole" ? 4.2 : 4.5;
      const stromVerbrauch = verbrauchKwh / cop;
      neueKosten = stromVerbrauch * stromPreis;
      // Aktualisierte Kosten 2024/2025: Luft-WP 37.000€, Sole-WP 50.000€, Wasser-WP 55.000€
      investition = waermepumpenTyp === "luft" ? 37000 : waermepumpenTyp === "sole" ? 50000 : 55000;
    } else if (neueHeizung === "pellet") {
      cop = 0.9;
      neueKosten = (verbrauchKwh / cop) * pelletPreis;
      // Aktualisierte Kosten 2024/2025: Pelletheizung 37.000€
      investition = 37000;
    } else if (neueHeizung === "fernwaerme") {
      cop = 1;
      neueKosten = verbrauchKwh * 0.10;
      investition = 15000;
    }

    const stromVerbrauch = neueHeizung === "waermepumpe" ? verbrauchKwh / cop : 0;
    const einsparungJahr = aktuelleKosten - neueKosten;

    const grundfoerderung = 30;
    const klimaGeschwindigkeitsBonus = klimaBonus ? 20 : 0;
    const einkommensBonusWert = einkommensBonus ? 30 : 0;
    const effizienzBonusWert = (effizienzBonus && neueHeizung === "waermepumpe") ? 5 : 0;
    // WICHTIG: iSFP-Bonus gilt NICHT für Heizungstausch! Nur für Gebäudehülle.

    let foerderProzent = grundfoerderung + klimaGeschwindigkeitsBonus + einkommensBonusWert + effizienzBonusWert;
    foerderProzent = Math.min(foerderProzent, 70);

    const we = Math.min(Math.max(parseInt(wohneinheiten) || 1, 1), 6);
    const maxFoerderfaehig = 30000 + (we - 1) * 15000;
    const foerderungBasis = Math.min(investition, maxFoerderfaehig);
    const foerderung = foerderungBasis * (foerderProzent / 100);
    const eigenanteil = investition - foerderung;

    const amortisation = einsparungJahr > 0 ? eigenanteil / einsparungJahr : 0;

    const co2AltProKwh = heizart === "gas" ? 0.2 : 0.266;
    const co2NeuProKwh = neueHeizung === "waermepumpe" ? 0.42 : neueHeizung === "pellet" ? 0.023 : 0.1;
    const co2Alt = verbrauchKwh * co2AltProKwh;
    const co2Neu = neueHeizung === "waermepumpe" ? stromVerbrauch * co2NeuProKwh : (verbrauchKwh / (cop || 1)) * co2NeuProKwh;
    const co2Einsparung = co2Alt - co2Neu;

    return {
      aktuelleKosten,
      neueKosten,
      einsparungJahr,
      cop,
      stromVerbrauch,
      investition,
      grundfoerderung,
      klimaGeschwindigkeitsBonus,
      einkommensBonusWert,
      effizienzBonusWert,
      foerderProzent,
      maxFoerderfaehig,
      foerderungBasis,
      foerderung,
      eigenanteil,
      amortisation,
      co2Einsparung,
      wohneinheitenAnzahl: we
    };
  }, [heizart, verbrauch, waermepumpenTyp, neueHeizung, wohneinheiten, klimaBonus, einkommensBonus, effizienzBonus]);

  return (
    <FadeInView>
