              <p className="text-xs text-muted-foreground">
                Typisch: EFH 15.000-25.000 kWh, ZFH 25.000-40.000 kWh
              </p>

              <div>
                <Label htmlFor="neueHeizung">Neue Heizungsart</Label>
                <Select value={neueHeizung} onValueChange={setNeueHeizung}>
