function SolarRechner() {
  const [dachflaeche, setDachflaeche] = useState("50");
  const [ausrichtung, setAusrichtung] = useState("sued");
  const [stromverbrauch, setStromverbrauch] = useState("4000");
  const [strompreis, setStrompreis] = useState("0.35");
  const [speicher, setSpeicher] = useState(false);

  const ergebnis = useMemo(() => {
    const flaeche = parseFloat(dachflaeche) || 50;
    const verbrauch = parseFloat(stromverbrauch) || 4000;
    const preis = parseFloat(strompreis) || 0.35;

    const kwpProQm = 0.2;
    const kwp = Math.min(flaeche * kwpProQm, 15);

    const ertragFaktoren: Record<string, number> = {
      sued: 1.0,
      suedost: 0.95,
      suedwest: 0.95,
      ost: 0.85,
      west: 0.85
    };
    const ertragProKwp = 950 * (ertragFaktoren[ausrichtung] || 1.0);
    const jahresertrag = kwp * ertragProKwp;

    const eigenverbrauchsanteil = speicher ? 0.60 : 0.30;
    const eigenverbrauch = Math.min(jahresertrag * eigenverbrauchsanteil, verbrauch);
    const einspeisung = jahresertrag - eigenverbrauch;

    const einspeiseverguetung = 0.081;
    const ersparnis = eigenverbrauch * preis + einspeisung * einspeiseverguetung;

    // Aktualisierte PV-Kosten 2024/2025: 2.200 €/kWp
    const preisProKwp = 2200;
    const speicherPreis = speicher ? 8000 : 0;
    const investition = kwp * preisProKwp + speicherPreis;

    const amortisation = ersparnis > 0 ? investition / ersparnis : 0;

    const co2ProKwh = 0.42;
    const co2Einsparung = eigenverbrauch * co2ProKwh;

    return {
      kwp,
      jahresertrag,
      eigenverbrauch,
      einspeisung,
      ersparnis,
      investition,
      amortisation,
      co2Einsparung,
      eigenverbrauchsanteil: eigenverbrauchsanteil * 100
    };
  }, [dachflaeche, ausrichtung, stromverbrauch, strompreis, speicher]);

  return (
    <FadeInView>
