}

function DaemmungsRechner() {
  const [massnahme, setMassnahme] = useState("fassade");
  const [flaeche, setFlaeche] = useState("150");
  const [uWertAlt, setUWertAlt] = useState("1.4");
  const [uWertNeu, setUWertNeu] = useState("0.2");
  const [energiepreis, setEnergiepreis] = useState("0.12");
  const [hatIsfp, setHatIsfp] = useState(false);

  const ergebnis = useMemo(() => {
    const flaecheQm = parseFloat(flaeche) || 150;
    const uAlt = parseFloat(uWertAlt) || 1.4;
    const uNeu = parseFloat(uWertNeu) || 0.2;
    const preis = parseFloat(energiepreis) || 0.12;

    const gradtagszahl = 2900;
    const heizstunden = 24;

    const transmissionAlt = uAlt * flaecheQm * gradtagszahl * heizstunden / 1000;
    const transmissionNeu = uNeu * flaecheQm * gradtagszahl * heizstunden / 1000;
    const einsparungKwh = transmissionAlt - transmissionNeu;
    const einsparungEuro = einsparungKwh * preis;

    // Aktualisierte Dämmkosten 2024/2025 (Gesamtkosten inkl. Montage)
    const kostenProQm: Record<string, number> = {
      fassade: 250,              // WDVS: 250 €/m²
      dach_schraege: 150,        // Unter-/Zwischensparrendämmung: 150 €/m²
      dach_flach: 400,           // Flachdachdämmung von oben: 400 €/m²
      oberste_geschossdecke: 50, // Nicht begehbar: 50 €/m², begehbar: 150 €/m²
      kellerdecke: 80,           // Kellerdecke von unten: 80 €/m²
      bodenplatte: 150,          // Kellerboden zum Erdreich: 150 €/m²
      fenster: 750,              // Dreifach-Verglasung: 750 €/m²
      tueren: 2790               // Haustür: 2.790 €/m²
    };
    const investition = flaecheQm * (kostenProQm[massnahme] || 180);

    let foerderProzent = 15;
    if (hatIsfp) foerderProzent += 5;

    const foerderung = investition * (foerderProzent / 100);
    const eigenanteil = investition - foerderung;

    const amortisation = einsparungEuro > 0 ? eigenanteil / einsparungEuro : 0;

    const co2Einsparung = einsparungKwh * 0.2;

    return {
      transmissionAlt,
      transmissionNeu,
      einsparungKwh,
      einsparungEuro,
      investition,
      foerderProzent,
      foerderung,
      eigenanteil,
      amortisation,
      co2Einsparung
    };
  }, [massnahme, flaeche, uWertAlt, uWertNeu, energiepreis, hatIsfp]);

  const massnahmenDefaults: Record<string, { uAlt: string; uNeu: string }> = {
    fassade: { uAlt: "1.4", uNeu: "0.2" },
    dach_schraege: { uAlt: "0.9", uNeu: "0.14" },
    dach_flach: { uAlt: "0.8", uNeu: "0.14" },
    oberste_geschossdecke: { uAlt: "0.9", uNeu: "0.14" },
    kellerdecke: { uAlt: "1.0", uNeu: "0.25" },
    bodenplatte: { uAlt: "1.2", uNeu: "0.30" },
    fenster: { uAlt: "2.8", uNeu: "0.9" },
    tueren: { uAlt: "3.5", uNeu: "1.3" }
  };

  const handleMassnahmeChange = (value: string) => {
    setMassnahme(value);
    const defaults = massnahmenDefaults[value];
    if (defaults) {
      setUWertAlt(defaults.uAlt);
      setUWertNeu(defaults.uNeu);
    }
  };

  return (
    <FadeInView>
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <Card className="p-6">
          <CardContent className="pt-0 space-y-6">
            <div>
              <h2 className="text-lg font-bold font-heading text-foreground mb-4">Eingaben</h2>
            </div>

            <div className="space-y-4">
              <div>
                <Label htmlFor="massnahme">Dämmmaßnahme</Label>
                <Select value={massnahme} onValueChange={handleMassnahmeChange}>
                  <SelectTrigger id="massnahme" data-testid="select-massnahme">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="fassade">Fassadendämmung (WDVS)</SelectItem>
                    <SelectItem value="dach_schraege">Dachschräge / Steildach</SelectItem>
                    <SelectItem value="dach_flach">Flachdach</SelectItem>
                    <SelectItem value="oberste_geschossdecke">Oberste Geschossdecke</SelectItem>
                    <SelectItem value="kellerdecke">Kellerdeckendämmung</SelectItem>
                    <SelectItem value="bodenplatte">Bodenplatte / Perimeterdämmung</SelectItem>
                    <SelectItem value="fenster">Fenster</SelectItem>
                    <SelectItem value="tueren">Haustür / Außentüren</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label htmlFor="flaeche">Fläche (m²)</Label>
                <Input
                  id="flaeche"
                  type="number"
                  value={flaeche}
                  onChange={(e) => setFlaeche(e.target.value)}
                  placeholder="z.B. 150"
                  data-testid="input-flaeche"
                />
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="uWertAlt">U-Wert alt (W/m²K)</Label>
                  <Input
                    id="uWertAlt"
                    type="number"
                    step="0.1"
                    value={uWertAlt}
                    onChange={(e) => setUWertAlt(e.target.value)}
                    data-testid="input-uwert-alt"
                  />
                </div>
                <div>
                  <Label htmlFor="uWertNeu">U-Wert neu (W/m²K)</Label>
                  <Input
                    id="uWertNeu"
                    type="number"
                    step="0.01"
                    value={uWertNeu}
                    onChange={(e) => setUWertNeu(e.target.value)}
                    data-testid="input-uwert-neu"
                  />
                </div>
              </div>

              <div>
                <Label htmlFor="energiepreis">Energiepreis (€/kWh)</Label>
                <Input
                  id="energiepreis"
                  type="number"
                  step="0.01"
                  value={energiepreis}
                  onChange={(e) => setEnergiepreis(e.target.value)}
                  data-testid="input-energiepreis"
                />
              </div>

              <div>
                <label className="flex items-center gap-2 cursor-pointer">
                  <input 
                    type="checkbox" 
                    checked={hatIsfp}
                    onChange={(e) => setHatIsfp(e.target.checked)}
                    className="rounded border-border"
                  />
                  <span className="text-sm text-foreground">iSFP-Bonus (+5%)</span>
                </label>
              </div>
            </div>
          </CardContent>
        </Card>

        <EmailGate
          calculatorType="daemmung"
          calculatorName="Dämmung"
          results={[
            { label: "Jährliche Einsparung", value: `${Math.round(ergebnis.einsparungEuro).toLocaleString('de-DE')} €` },
            { label: "Ihr Eigenanteil", value: `${Math.round(ergebnis.eigenanteil).toLocaleString('de-DE')} €` },
            { label: "Förderung", value: `${Math.round(ergebnis.foerderung).toLocaleString('de-DE')} € (${ergebnis.foerderProzent}%)` },
            { label: "Amortisation", value: `${ergebnis.amortisation.toFixed(1)} Jahre` },
          ]}
          inputs={[
            { label: "Dämmmaßnahme", value: massnahme === "fassade" ? "Fassadendämmung" : massnahme === "dach_schraege" ? "Dachschräge" : massnahme === "dach_flach" ? "Flachdach" : massnahme === "oberste_geschossdecke" ? "Oberste Geschossdecke" : massnahme === "kellerdecke" ? "Kellerdecke" : massnahme === "bodenplatte" ? "Bodenplatte" : massnahme === "fenster" ? "Fenster" : "Haustür" },
            { label: "Fläche", value: `${parseFloat(flaeche).toLocaleString('de-DE')} m²` },
            { label: "U-Wert (alt/neu)", value: `${uWertAlt} / ${uWertNeu} W/m²K` },
          ]}
        >
          <Card className="p-6 bg-primary/5 border-primary/20">
            <CardContent className="pt-0 space-y-6">
              <div>
                <h2 className="text-lg font-bold font-heading text-foreground mb-4">Ergebnis</h2>
              </div>

              <div className="space-y-4">
