function KomplettsanierungRechner() {
  const [wohnflaeche, setWohnflaeche] = useState("150");
  const [baujahr, setBaujahr] = useState("1970");
  const [zielstandard, setZielstandard] = useState("eh70");
  const [hatIsfp, setHatIsfp] = useState(true);

  const ergebnis = useMemo(() => {
    const flaeche = parseFloat(wohnflaeche) || 150;
    const jahr = parseInt(baujahr) || 1970;

    const verbrauchProQm: Record<string, number> = {
      "vor1950": 250,
      "1950-1969": 200,
      "1970-1979": 180,
      "1980-1994": 150,
      "1995-2001": 120,
      "nach2001": 80
    };

    let zeitraum = "1970-1979";
    if (jahr < 1950) zeitraum = "vor1950";
    else if (jahr < 1970) zeitraum = "1950-1969";
    else if (jahr < 1980) zeitraum = "1970-1979";
    else if (jahr < 1995) zeitraum = "1980-1994";
    else if (jahr < 2002) zeitraum = "1995-2001";
    else zeitraum = "nach2001";

    const verbrauchAlt = flaeche * (verbrauchProQm[zeitraum] || 180);

    const zielVerbrauch: Record<string, number> = {
      "eh85": 85,
      "eh70": 70,
      "eh55": 55,
      "eh40": 40
    };
    const verbrauchNeu = flaeche * (zielVerbrauch[zielstandard] || 70);

    const einsparungKwh = verbrauchAlt - verbrauchNeu;
    const energiepreis = 0.12;
    const einsparungEuro = einsparungKwh * energiepreis;

    const kostenProQm: Record<string, number> = {
      "eh85": 400,
      "eh70": 550,
      "eh55": 750,
      "eh40": 1000
    };
    const investition = flaeche * (kostenProQm[zielstandard] || 550);

    const foerderProQm: Record<string, number> = {
      "eh85": 0,
      "eh70": 10000,
      "eh55": 15000,
      "eh40": 20000
    };
    let basisFoerderung = foerderProQm[zielstandard] || 10000;
    if (hatIsfp) basisFoerderung *= 1.05;
    const foerderung = Math.min(basisFoerderung, investition * 0.4);

    const eigenanteil = investition - foerderung;
    const amortisation = einsparungEuro > 0 ? eigenanteil / einsparungEuro : 0;

    const co2Einsparung = einsparungKwh * 0.2;

    return {
      verbrauchAlt,
      verbrauchNeu,
      einsparungKwh,
      einsparungEuro,
      investition,
      foerderung,
      eigenanteil,
      amortisation,
      co2Einsparung,
      einsparungProzent: ((verbrauchAlt - verbrauchNeu) / verbrauchAlt * 100).toFixed(0)
    };
  }, [wohnflaeche, baujahr, zielstandard, hatIsfp]);

  return (
    <FadeInView>
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <Card className="p-6">
          <CardContent className="pt-0 space-y-6">
            <div>
              <h2 className="text-lg font-bold font-heading text-foreground mb-4">Eingaben</h2>
            </div>

            <div className="space-y-4">
              <div>
                <Label htmlFor="wohnflaeche">Wohnfläche (m²)</Label>
                <Input
                  id="wohnflaeche"
                  type="number"
                  value={wohnflaeche}
                  onChange={(e) => setWohnflaeche(e.target.value)}
                  placeholder="z.B. 150"
                  data-testid="input-wohnflaeche"
                />
              </div>

              <div>
                <Label htmlFor="baujahr">Baujahr</Label>
                <Input
                  id="baujahr"
                  type="number"
                  value={baujahr}
                  onChange={(e) => setBaujahr(e.target.value)}
                  placeholder="z.B. 1970"
                  data-testid="input-baujahr"
                />
              </div>

              <div>
                <Label htmlFor="zielstandard">Ziel-Effizienzstandard</Label>
                <Select value={zielstandard} onValueChange={setZielstandard}>
                  <SelectTrigger id="zielstandard" data-testid="select-zielstandard">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="eh85">Effizienzhaus 85 (85 kWh/m²a)</SelectItem>
                    <SelectItem value="eh70">Effizienzhaus 70 (70 kWh/m²a)</SelectItem>
                    <SelectItem value="eh55">Effizienzhaus 55 (55 kWh/m²a)</SelectItem>
                    <SelectItem value="eh40">Effizienzhaus 40 (40 kWh/m²a)</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <label className="flex items-center gap-2 cursor-pointer">
                  <input 
                    type="checkbox" 
                    checked={hatIsfp}
                    onChange={(e) => setHatIsfp(e.target.checked)}
                    className="rounded border-border"
                  />
                  <span className="text-sm text-foreground">Mit iSFP (Individueller Sanierungsfahrplan)</span>
                </label>
                <p className="text-xs text-muted-foreground ml-6 mt-1">
                  Erhöht die Förderung um 5%
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <EmailGate
          calculatorType="komplett"
