          results={[
            { label: "Jährliche Einsparung", value: `${Math.round(ergebnis.einsparungEuro).toLocaleString('de-DE')} €` },
            { label: "Ihr Eigenanteil", value: `${Math.round(ergebnis.eigenanteil).toLocaleString('de-DE')} €` },
