function PMCashflowRechner() {
  const [mieteinnahmenMonat, setMieteinnahmenMonat] = useState("1200");
  const [nebenkosten, setNebenkosten] = useState("250");
  const [ruecklage, setRuecklage] = useState("150");
  const [verwaltungskosten, setVerwaltungskosten] = useState("35");
  const [versicherung, setVersicherung] = useState("80");
  const [grundsteuer, setGrundsteuer] = useState("60");
  const [instandhaltung, setInstandhaltung] = useState("100");
  const [zinskosten, setZinskosten] = useState("400");
  const [tilgung, setTilgung] = useState("300");
  const [leerstandsrisiko, setLeerstandsrisiko] = useState("3");

  const ergebnis = useMemo(() => {
    const einnahmen = parseFloat(mieteinnahmenMonat) || 0;
    const nk = parseFloat(nebenkosten) || 0;
    const rueck = parseFloat(ruecklage) || 0;
    const verwaltung = parseFloat(verwaltungskosten) || 0;
    const vers = parseFloat(versicherung) || 0;
    const steuer = parseFloat(grundsteuer) || 0;
    const instand = parseFloat(instandhaltung) || 0;
    const zinsen = parseFloat(zinskosten) || 0;
    const tilg = parseFloat(tilgung) || 0;
    const leerstand = parseFloat(leerstandsrisiko) || 0;

    const bruttoEinnahmenMonat = einnahmen + nk;
    const bruttoEinnahmenJahr = bruttoEinnahmenMonat * 12;

    const leerstandAbzug = (einnahmen * (leerstand / 100));
    const nettoMieteMonat = einnahmen - leerstandAbzug;

    const betriebskostenMonat = verwaltung + vers + steuer + instand;
    const betriebskostenJahr = betriebskostenMonat * 12;

    const ruecklageJahr = rueck * 12;

    const kapitaldienstMonat = zinsen + tilg;
    const kapitaldienstJahr = kapitaldienstMonat * 12;

    const cashflowVorSteuerMonat = nettoMieteMonat - betriebskostenMonat - rueck - kapitaldienstMonat;
    const cashflowVorSteuerJahr = cashflowVorSteuerMonat * 12;

    const vermoegenszuwachsMonat = tilg;
    const vermoegenszuwachsJahr = vermoegenszuwachsMonat * 12;

    const gesamtertragMonat = cashflowVorSteuerMonat + vermoegenszuwachsMonat;
    const gesamtertragJahr = gesamtertragMonat * 12;

    const cashflowRendite = einnahmen > 0 ? (cashflowVorSteuerMonat / einnahmen * 100) : 0;

    return {
      bruttoEinnahmenMonat,
      bruttoEinnahmenJahr,
      nettoMieteMonat,
      leerstandAbzug,
      betriebskostenMonat,
      betriebskostenJahr,
      ruecklageJahr,
      kapitaldienstMonat,
      kapitaldienstJahr,
      cashflowVorSteuerMonat,
      cashflowVorSteuerJahr,
      vermoegenszuwachsMonat,
      vermoegenszuwachsJahr,
      gesamtertragMonat,
      gesamtertragJahr,
      cashflowRendite
    };
  }, [mieteinnahmenMonat, nebenkosten, ruecklage, verwaltungskosten, versicherung, grundsteuer, instandhaltung, zinskosten, tilgung, leerstandsrisiko]);

  return (
