function RenditeRechner() {
  const [kaufpreis, setKaufpreis] = useState("350000");
  const [kaufnebenkosten, setKaufnebenkosten] = useState("12");
  const [eigenkapital, setEigenkapital] = useState("80000");
  const [jahresmiete, setJahresmiete] = useState("14400");
  const [betriebskosten, setBetriebskosten] = useState("2400");
  const [instandhaltung, setInstandhaltung] = useState("1200");
  const [verwaltung, setVerwaltung] = useState("600");
  const [zinssatz, setZinssatz] = useState("3.5");
  const [wertsteigerung, setWertsteigerung] = useState("2");

  const ergebnis = useMemo(() => {
    const kp = parseFloat(kaufpreis) || 0;
    const knProzent = parseFloat(kaufnebenkosten) || 0;
    const ek = parseFloat(eigenkapital) || 0;
    const jm = parseFloat(jahresmiete) || 0;
    const bk = parseFloat(betriebskosten) || 0;
    const ih = parseFloat(instandhaltung) || 0;
    const vw = parseFloat(verwaltung) || 0;
    const zins = parseFloat(zinssatz) || 0;
    const ws = parseFloat(wertsteigerung) || 0;

    const kaufnebenkostenBetrag = kp * (knProzent / 100);
    const gesamtinvestition = kp + kaufnebenkostenBetrag;
    const fremdkapital = gesamtinvestition - ek;
    const zinskosten = fremdkapital * (zins / 100);

    const gesamtkosten = bk + ih + vw + zinskosten;
    const reinertrag = jm - gesamtkosten;

    const bruttomietrendite = kp > 0 ? (jm / kp) * 100 : 0;
    const nettomietrendite = kp > 0 ? ((jm - bk - ih - vw) / gesamtinvestition) * 100 : 0;
    const eigenkapitalrendite = ek > 0 ? (reinertrag / ek) * 100 : 0;

    const jahresWertsteigerung = kp * (ws / 100);
    const totalReturn = ek > 0 ? ((reinertrag + jahresWertsteigerung) / ek) * 100 : 0;

    const mietmultiplikator = jm > 0 ? kp / jm : 0;

    return {
      kaufnebenkostenBetrag: Math.round(kaufnebenkostenBetrag),
      gesamtinvestition: Math.round(gesamtinvestition),
      fremdkapital: Math.round(fremdkapital),
      zinskosten: Math.round(zinskosten),
      gesamtkosten: Math.round(gesamtkosten),
      reinertrag: Math.round(reinertrag),
      bruttomietrendite,
      nettomietrendite,
      eigenkapitalrendite,
      jahresWertsteigerung: Math.round(jahresWertsteigerung),
      totalReturn,
      mietmultiplikator
    };
  }, [kaufpreis, kaufnebenkosten, eigenkapital, jahresmiete, betriebskosten, instandhaltung, verwaltung, zinssatz, wertsteigerung]);

  return (
    <FadeInView>
