                  <div className="bg-background rounded-lg p-4">
                    <p className="text-xs text-muted-foreground mb-1">Fremdkapital</p>
                    <p className="text-lg font-bold text-foreground">{ergebnis.fremdkapital.toLocaleString('de-DE')} EUR</p>
                    <p className="text-xs text-muted-foreground">Zins: {ergebnis.zinskosten.toLocaleString('de-DE')} EUR/Jahr</p>
                  </div>
                </div>

                <div className="bg-background rounded-lg p-4">
                  <div className="flex items-center gap-2 mb-2">
                    <TrendingDown className="w-5 h-5 text-copper rotate-180" />
                    <p className="font-semibold text-foreground">Bruttomietrendite</p>
                  </div>
                  <p className="text-2xl font-bold text-copper">{ergebnis.bruttomietrendite.toFixed(2)} %</p>
                  <p className="text-xs text-muted-foreground">Jahresmiete / Kaufpreis</p>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div className="bg-background rounded-lg p-4">
                    <p className="text-xs text-muted-foreground mb-1">Nettomietrendite</p>
                    <p className="text-xl font-bold text-foreground">{ergebnis.nettomietrendite.toFixed(2)} %</p>
                    <p className="text-xs text-muted-foreground">nach Kosten, vor Zins</p>
                  </div>
                  <div className="bg-background rounded-lg p-4">
                    <p className="text-xs text-muted-foreground mb-1">Mietmultiplikator</p>
                    <p className="text-xl font-bold text-foreground">{ergebnis.mietmultiplikator.toFixed(1)}x</p>
                    <p className="text-xs text-muted-foreground">Kaufpreis / Jahresmiete</p>
                  </div>
                </div>

                <div className={`rounded-lg p-4 ${ergebnis.eigenkapitalrendite >= 0 ? 'bg-primary text-white' : 'bg-destructive text-white'}`}>
                  <p className="text-xs opacity-80 mb-1">Eigenkapitalrendite (vor Steuern)</p>
                  <p className="text-2xl font-bold">{ergebnis.eigenkapitalrendite.toFixed(2)} %</p>
                  <p className="text-sm opacity-80">Reinertrag: {ergebnis.reinertrag.toLocaleString('de-DE')} EUR/Jahr</p>
                </div>

                <div className="bg-copper/10 border border-copper/30 rounded-lg p-4">
                  <p className="text-xs text-copper font-semibold mb-1">Total Return (inkl. Wertsteigerung)</p>
                  <p className="text-xl font-bold text-copper">{ergebnis.totalReturn.toFixed(2)} %</p>
                  <p className="text-xs text-copper/80">+{ergebnis.jahresWertsteigerung.toLocaleString('de-DE')} EUR Wertzuwachs p.a.</p>
                </div>
              </div>

              <div className="bg-background/60 rounded-lg p-4 space-y-2">
                <p className="text-xs font-semibold text-foreground">Berechnungsformeln:</p>
                <div className="text-xs text-muted-foreground space-y-1">
                  <p>Bruttomietrendite = Jahresmiete / Kaufpreis * 100</p>
                  <p>Nettomietrendite = (Jahresmiete - Kosten) / Gesamtinvestition * 100</p>
                  <p>Eigenkapitalrendite = Reinertrag / Eigenkapital * 100</p>
                  <p>Total Return = (Reinertrag + Wertsteigerung) / Eigenkapital * 100</p>
                </div>
              </div>
            </CardContent>
          </Card>
        </EmailGate>
      </div>
    </FadeInView>
  );
}

function BaukostenRechner() {
  const [wohnflaeche, setWohnflaeche] = useState("150");
  const [sanierungstiefe, setSanierungstiefe] = useState("standard");
  const [massnahmen, setMassnahmen] = useState({
    dach: true,
    fassade: true,
    fenster: true,
    heizung: true,
    elektro: false,
    sanitaer: false,
    innenausbau: false
  });

  // Aktualisierte Sanierungskosten 2024/2025 (Gesamtkosten inkl. Montage)
  const kostenProQm = {
    dach: { minimal: 50, standard: 150, premium: 450 },      // OG-Decke 50€, Zwischensparren 150€, Aufsparren+Eindeckung 450€
    fassade: { minimal: 40, standard: 250, premium: 280 },    // Einblas 40€, WDVS 250€, Premium 280€
    fenster: { minimal: 500, standard: 750, premium: 900 },   // Dreifach-Verglasung: 750 €/m²
    heizung: { minimal: 150, standard: 250, premium: 350 },   // Luft-WP ~37.000€ = ~250€/m² bei 150m²
    elektro: { minimal: 60, standard: 100, premium: 180 },
    sanitaer: { minimal: 50, standard: 120, premium: 200 },
    innenausbau: { minimal: 80, standard: 180, premium: 350 }
  };

  const labels = {
    dach: "Dachdämmung/-sanierung",
    fassade: "Fassadendämmung (WDVS)",
    fenster: "Fenster & Türen",
    heizung: "Heizungstausch",
    elektro: "Elektroinstallation",
    sanitaer: "Sanitärinstallation",
    innenausbau: "Innenausbau"
  };

  const ergebnis = useMemo(() => {
    const qm = parseFloat(wohnflaeche) || 0;
    const tiefe = sanierungstiefe as 'minimal' | 'standard' | 'premium';

    let gesamtkosten = 0;
    const einzelkosten: {name: string; kosten: number}[] = [];

    Object.entries(massnahmen).forEach(([key, aktiv]) => {
      if (aktiv) {
        const kostenKey = key as keyof typeof kostenProQm;
        const kosten = kostenProQm[kostenKey][tiefe] * qm;
        gesamtkosten += kosten;
        einzelkosten.push({
          name: labels[kostenKey],
          kosten
        });
      }
    });

    const foerderfaehig = einzelkosten.filter(e => 
      ["Dachdämmung/-sanierung", "Fassadendämmung (WDVS)", "Fenster & Türen", "Heizungstausch"].includes(e.name)
    ).reduce((sum, e) => sum + e.kosten, 0);

    const maxFoerderung = Math.min(foerderfaehig * 0.70, 30000);
    const nettokosten = gesamtkosten - maxFoerderung;
    const kostenProQmGesamt = qm > 0 ? gesamtkosten / qm : 0;

    return {
      einzelkosten,
      gesamtkosten: Math.round(gesamtkosten),
      foerderfaehig: Math.round(foerderfaehig),
      maxFoerderung: Math.round(maxFoerderung),
      nettokosten: Math.round(nettokosten),
      kostenProQmGesamt: Math.round(kostenProQmGesamt)
    };
  }, [wohnflaeche, sanierungstiefe, massnahmen]);

  return (
    <FadeInView>
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <Card className="p-6">
          <CardContent className="pt-0 space-y-5">
            <div>
              <h2 className="text-lg font-bold font-heading text-foreground mb-1">Sanierungskosten-Schätzer</h2>
              <p className="text-sm text-muted-foreground">Groben Kostenrahmen für Ihr Sanierungsprojekt ermitteln</p>
            </div>

            <div className="p-3 bg-surface rounded-lg">
              <Label className="font-semibold mb-2 block">Gebäudedaten</Label>
              <div className="grid grid-cols-2 gap-3">
                <div>
                  <Label htmlFor="wohnflaeche" className="text-xs">Wohnfläche (m²)</Label>
                  <Input
                    id="wohnflaeche"
                    type="number"
                    value={wohnflaeche}
                    onChange={(e) => setWohnflaeche(e.target.value)}
                    data-testid="input-baukosten-flaeche"
                  />
                </div>
                <div>
                  <Label htmlFor="sanierungstiefe" className="text-xs">Sanierungstiefe</Label>
                  <Select value={sanierungstiefe} onValueChange={setSanierungstiefe}>
                    <SelectTrigger data-testid="select-sanierungstiefe">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="minimal">Minimal (günstig)</SelectItem>
                      <SelectItem value="standard">Standard (mittel)</SelectItem>
                      <SelectItem value="premium">Premium (hochwertig)</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>
            </div>

            <div className="p-3 bg-surface rounded-lg">
              <Label className="font-semibold mb-3 block">Geplante Maßnahmen</Label>
              <div className="grid grid-cols-1 gap-2">
                {Object.entries(massnahmen).map(([key, checked]) => (
                  <label key={key} className="flex items-center gap-3 p-2 rounded-lg hover:bg-background cursor-pointer">
                    <input
                      type="checkbox"
                      checked={checked}
                      onChange={(e) => setMassnahmen(prev => ({ ...prev, [key]: e.target.checked }))}
                      className="w-4 h-4 rounded border-border text-primary focus:ring-primary"
                      data-testid={`checkbox-${key}`}
                    />
                    <span className="text-sm text-foreground">{labels[key as keyof typeof labels]}</span>
                    <span className="text-xs text-muted-foreground ml-auto">
                      {kostenProQm[key as keyof typeof kostenProQm][sanierungstiefe as 'minimal' | 'standard' | 'premium']} EUR/m²
                    </span>
                  </label>
                ))}
              </div>
            </div>
          </CardContent>
        </Card>

        <EmailGate
          calculatorType="baukosten"
          calculatorName="Sanierungskosten"
          results={[
            { label: "Gesamtkosten", value: `${ergebnis.gesamtkosten.toLocaleString('de-DE')} €` },
            { label: "Max. Förderung", value: `${ergebnis.maxFoerderung.toLocaleString('de-DE')} €` },
            { label: "Nettokosten", value: `${ergebnis.nettokosten.toLocaleString('de-DE')} €` },
            { label: "Kosten pro m²", value: `${ergebnis.kostenProQmGesamt.toLocaleString('de-DE')} €/m²` },
          ]}
          inputs={[
            { label: "Wohnfläche", value: `${parseFloat(wohnflaeche).toLocaleString('de-DE')} m²` },
            { label: "Sanierungstiefe", value: sanierungstiefe === "minimal" ? "Minimal" : sanierungstiefe === "standard" ? "Standard" : "Premium" },
            { label: "Maßnahmen", value: ergebnis.einzelkosten.map(e => e.name).join(", ") },
          ]}
        >
          <Card className="p-6 bg-blue-500/5 border-blue-500/20">
            <CardContent className="pt-0 space-y-6">
              <div>
                <h2 className="text-lg font-bold font-heading text-foreground mb-4">Kostenschätzung</h2>
              </div>

              <div className="space-y-4">
                {ergebnis.einzelkosten.length > 0 && (
                  <div className="bg-background rounded-lg p-4 space-y-2">
                    <p className="text-xs text-muted-foreground mb-2">Einzelkosten (Schätzung)</p>
                    {ergebnis.einzelkosten.map((pos, idx) => (
                      <div key={idx} className="flex justify-between text-sm">
                        <span className="text-foreground">{pos.name}</span>
                        <span className="font-medium text-foreground">{pos.kosten.toLocaleString('de-DE')} EUR</span>
                      </div>
                    ))}
                  </div>
                )}

                <div className="bg-background rounded-lg p-4">
                  <div className="flex items-center gap-2 mb-2">
                    <Calculator className="w-5 h-5 text-blue-600" />
                    <p className="font-semibold text-foreground">Gesamtkosten (brutto)</p>
                  </div>
                  <p className="text-2xl font-bold text-foreground">{ergebnis.gesamtkosten.toLocaleString('de-DE')} EUR</p>
                  <p className="text-sm text-muted-foreground">{ergebnis.kostenProQmGesamt.toLocaleString('de-DE')} EUR/m²</p>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div className="bg-background rounded-lg p-4">
                    <p className="text-xs text-muted-foreground mb-1">Förderfähig (BEG)</p>
                    <p className="text-lg font-bold text-foreground">{ergebnis.foerderfaehig.toLocaleString('de-DE')} EUR</p>
                  </div>
                  <div className="bg-background rounded-lg p-4">
                    <p className="text-xs text-muted-foreground mb-1">Max. Förderung</p>
                    <p className="text-lg font-bold text-primary">-{ergebnis.maxFoerderung.toLocaleString('de-DE')} EUR</p>
                    <p className="text-xs text-muted-foreground">bis zu 70%</p>
                  </div>
                </div>

                <div className="bg-blue-500/10 border border-blue-500/30 rounded-lg p-4">
                  <p className="text-xs text-blue-600 font-semibold mb-1">Geschätzte Nettokosten</p>
                  <p className="text-xl font-bold text-blue-600">{ergebnis.nettokosten.toLocaleString('de-DE')} EUR</p>
                  <p className="text-xs text-blue-600/80">nach maximaler Förderung</p>
                </div>
              </div>

              <div className="bg-background/60 rounded-lg p-4 space-y-2">
                <p className="text-xs font-semibold text-foreground">Hinweis:</p>
                <p className="text-xs text-muted-foreground">
                  Diese Schätzung basiert auf Durchschnittswerten und kann je nach Gebäudezustand, 
                  Region und Handwerkerauslastung stark variieren. Für ein verbindliches Angebot 
                  empfehlen wir einen Vor-Ort-Termin.
                </p>
              </div>
            </CardContent>
          </Card>
        </EmailGate>
      </div>
    </FadeInView>
  );
}

