import { useState } from "react";
import { SEO } from "@/components/SEO";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Checkbox } from "@/components/ui/checkbox";
import { Link } from "wouter";
import { 
  CheckCircle2, 
  AlertTriangle, 
  FileText, 
  Home, 
  Thermometer,
  Droplets,
  Zap,
  Wrench,
  Clock,
  Euro,
  Shield,
  ArrowRight,
  Download,
  Camera,
  Search,
  Eye,
  Calendar,
  Building2,
  Flame
} from "lucide-react";
import jsPDF from "jspdf";
import leafLogo from "@assets/Bildschirmfoto_2025-12-20_um_11.31.01_1766226677352.png";

export default function ChecklisteKaeufer() {
  const [checkedItems, setCheckedItems] = useState<Record<string, boolean>>({});

  const toggleItem = (sectionIndex: number, itemIndex: number) => {
    const key = `${sectionIndex}-${itemIndex}`;
    setCheckedItems(prev => ({ ...prev, [key]: !prev[key] }));
  };

  const getProgress = () => {
    const totalItems = checklistSections.reduce((acc, section) => acc + section.items.length, 0);
    const checkedCount = Object.values(checkedItems).filter(Boolean).length;
    return { checked: checkedCount, total: totalItems, percentage: Math.round((checkedCount / totalItems) * 100) };
  };

  const exportToPDF = async () => {
    const doc = new jsPDF();
    const progress = getProgress();

    doc.setFillColor(5, 100, 70);
    doc.rect(0, 0, 210, 40, 'F');

    // Add leaf logo
    try {
      const img = new Image();
      img.crossOrigin = "anonymous";
      await new Promise((resolve, reject) => {
        img.onload = resolve;
        img.onerror = reject;
        img.src = leafLogo;
      });
      doc.addImage(img, 'PNG', 15, 10, 20, 20);
    } catch (e) {
      console.log('Logo could not be loaded');
    }

    doc.setTextColor(255, 255, 255);
    doc.setFontSize(24);
    doc.setFont("helvetica", "bold");
    doc.text("PALVAR", 40, 20);
    doc.setFontSize(12);
    doc.setFont("helvetica", "normal");
    doc.text("Energieberatung | Projektmanagement | Gebaudeservices", 40, 30);

    doc.setTextColor(0, 0, 0);
    doc.setFontSize(18);
    doc.setFont("helvetica", "bold");
    doc.text("Checkliste fur Immobilienkaufer", 20, 55);

    doc.setFontSize(11);
    doc.setFont("helvetica", "normal");
    doc.setTextColor(100, 100, 100);
    doc.text(`Erstellt am: ${new Date().toLocaleDateString('de-DE')}`, 20, 65);
    doc.text(`Fortschritt: ${progress.checked} von ${progress.total} Punkten (${progress.percentage}%)`, 20, 72);

    doc.setDrawColor(5, 100, 70);
    doc.setFillColor(230, 245, 240);
    doc.roundedRect(20, 78, 170 * (progress.percentage / 100), 6, 2, 2, 'F');
    doc.roundedRect(20, 78, 170, 6, 2, 2, 'S');

    let yPosition = 95;

    checklistSections.forEach((section, sectionIndex) => {
      if (yPosition > 260) {
        doc.addPage();
        yPosition = 20;
      }

      doc.setFillColor(5, 100, 70);
      doc.setTextColor(255, 255, 255);
      doc.setFontSize(12);
      doc.setFont("helvetica", "bold");
      doc.roundedRect(20, yPosition - 5, 170, 8, 2, 2, 'F');
      doc.text(section.title, 25, yPosition + 1);
      yPosition += 12;

      section.items.forEach((item, itemIndex) => {
        if (yPosition > 270) {
          doc.addPage();
          yPosition = 20;
        }

        const key = `${sectionIndex}-${itemIndex}`;
        const isChecked = checkedItems[key] || false;

        doc.setTextColor(0, 0, 0);
        doc.setFontSize(10);
        doc.setFont("helvetica", "normal");

        if (isChecked) {
          doc.setFillColor(5, 100, 70);
          doc.rect(22, yPosition - 3, 4, 4, 'F');
          doc.setTextColor(5, 100, 70);
        } else {
          doc.setDrawColor(150, 150, 150);
          doc.rect(22, yPosition - 3, 4, 4, 'S');
          doc.setTextColor(0, 0, 0);
        }

        const statusText = isChecked ? " [Erledigt]" : "";
        const importantText = item.important ? " (Wichtig)" : "";
        doc.text(`${item.text}${importantText}${statusText}`, 30, yPosition);
        yPosition += 7;
      });

      yPosition += 5;
    });

    doc.addPage();
    doc.setFillColor(255, 240, 240);
    doc.rect(0, 0, 210, 297, 'F');

    doc.setTextColor(180, 0, 0);
    doc.setFontSize(16);
    doc.setFont("helvetica", "bold");
    doc.text("Warnsignale - Vorsicht geboten!", 20, 25);

    doc.setTextColor(100, 0, 0);
    doc.setFontSize(10);
    doc.setFont("helvetica", "normal");
    yPosition = 40;

    redFlags.forEach((flag) => {
      doc.text("• " + flag.text, 25, yPosition);
      yPosition += 8;
    });

    doc.setFillColor(5, 100, 70);
    doc.rect(0, 270, 210, 27, 'F');
    doc.setTextColor(255, 255, 255);
    doc.setFontSize(10);
    doc.text("PALVAR - Ihr Partner fur energieeffiziente Immobilien", 20, 280);
    doc.text("www.palvar.de | info@palvar.de", 20, 287);

    doc.save("PALVAR_Kaeufer_Checkliste.pdf");
  };
  const checklistSections = [
    {
      title: "Vor der Besichtigung",
      icon: Search,
      color: "bg-blue-500",
      items: [
        {
          text: "Energieausweis anfordern",
          important: true,
          details: "Gesetzlich vorgeschrieben bei Verkauf. Achten Sie auf Energieeffizienzklasse (A+ bis H) und Endenergiebedarf."
        },
        {
          text: "Baujahr und Baustil recherchieren",
          important: false,
          details: "Typische Schwachstellen je nach Baualter kennen (z.B. 60er: keine Dämmung, 70er: Flachdächer problematisch)."
        },
        {
          text: "Lage und Ausrichtung prüfen",
          important: false,
          details: "Südausrichtung für Solarnutzung? Verschattung durch Nachbargebäude? Lärmbelastung?"
        },
        {
          text: "Unterlagen zur Immobilie sammeln",
          important: true,
          details: "Grundrisse, Baugenehmigungen, Modernisierungsnachweise, Nebenkostenabrechnungen der letzten 3 Jahre."
        }
      ]
    },
    {
      title: "Gebäudehülle prüfen",
      icon: Home,
      color: "bg-primary",
      items: [
        {
          text: "Dachzustand bewerten",
          important: true,
          details: "Alter der Eindeckung? Dämmung vorhanden? Wasserspuren auf dem Dachboden? Zustand der Unterspannbahn?"
        },
        {
          text: "Fassade auf Risse und Schäden untersuchen",
          important: true,
          details: "Putzrisse können auf Setzungen hinweisen. Algenbefall zeigt Wärmebrücken. Fugen bei Klinkerfassaden prüfen."
        },
        {
          text: "Fenster und Türen checken",
          important: true,
          details: "Alter und Verglasung (Einscheiben, Doppel- oder Dreifachverglasung)? Dichtungen intakt? Kondenswasser zwischen Scheiben?"
        },
        {
          text: "Kellerdecke und Wände begutachten",
          important: false,
          details: "Feuchtigkeit? Salzausblühungen? Gedämmt? Zugang zur Unterseite möglich?"
        },
        {
          text: "Auf Wärmebrücken achten",
          important: true,
          details: "Kalte Ecken, Schimmelspuren, feuchte Stellen - besonders in Fensterleibungen und Außenecken."
        }
      ]
    },
    {
      title: "Heizung & Technik",
      icon: Flame,
      color: "bg-orange-500",
      items: [
        {
          text: "Heizungsanlage dokumentieren",
          important: true,
          details: "Alter, Typ (Öl, Gas, Wärmepumpe), Brennwert oder nicht? Bei >20 Jahren: Austausch einplanen!"
        },
        {
          text: "Heizkessel-Protokoll einsehen",
          important: false,
          details: "Wartungshistorie, Schornsteinfeger-Protokolle der letzten Jahre. Grenzwerte eingehalten?"
        },
        {
          text: "Heizkörper und Rohrleitungen prüfen",
          important: false,
          details: "Alter der Heizkörper? Thermostatventile vorhanden? Leitungen gedämmt?"
        },
        {
          text: "Warmwasserbereitung klären",
          important: false,
          details: "Zentral oder dezentral? Speicher oder Durchlauferhitzer? Zustand und Alter?"
        },
        {
          text: "Elektroinstallation bewerten",
          important: true,
          details: "Alter der Installation? FI-Schutzschalter? Ausreichend Stromkreise? Zustand Sicherungskasten?"
        }
      ]
    },
    {
      title: "Feuchtigkeit & Schimmel",
      icon: Droplets,
      color: "bg-cyan-500",
      items: [
        {
          text: "Keller auf Feuchtigkeit prüfen",
          important: true,
          details: "Wasserflecken, Salzausblühungen, muffiger Geruch? Horizontalsperre vorhanden?"
        },
        {
          text: "Schimmelbefall suchen",
          important: true,
          details: "Hinter Möbeln, in Ecken, unter Fensterbänken, in Bädern - auch versteckten Schimmel prüfen!"
        },
        {
          text: "Lüftungssituation bewerten",
          important: false,
          details: "Fenster mit Lüftungsstellung? Abluft in Bad/Küche? Kontrollierte Wohnraumlüftung?"
        },
        {
          text: "Dachstuhl auf Wasserschäden prüfen",
          important: true,
          details: "Verfärbungen am Holz, tropfende Stellen bei Regen, Zustand der Dämmung?"
        }
      ]
    },
    {
      title: "Dokumente & Unterlagen",
      icon: FileText,
      color: "bg-purple-500",
      items: [
        {
          text: "Energieausweis (gültig 10 Jahre)",
