                    </CardContent>
                  </Card>
                </a>
              ))}
            </div>
          </div>
        </section>

        {/* Detailed Building Components */}
        <section className="py-12">
          <div className="max-w-[1920px] mx-auto px-4 sm:px-6 lg:px-8 space-y-16">
            {buildingComponents.map((component, index) => (
              <div key={component.id} id={`${component.id}-detail`} className="scroll-mt-32">
