                  {/* Förderinfo */}
                  {component.foerderung && (
                    <div className="bg-primary/5 dark:bg-primary/10 rounded-xl p-5 mb-8 border border-primary/20">
                      <div className="flex items-center gap-2 mb-4">
                        <Euro className="w-5 h-5 text-primary" />
                        <h3 className="font-heading font-semibold text-foreground">Förderung</h3>
                      </div>
                      <div className={`grid ${component.foerderung.mitIsfp !== "-" ? "grid-cols-3" : "grid-cols-2"} gap-4 mb-4`}>
                        <div className="text-center">
                          <div className="text-2xl font-bold text-primary">{component.foerderung.bafa}</div>
                          <div className="text-xs text-muted-foreground">{component.foerderung.mitIsfp !== "-" ? "BAFA Basis" : "Fördersatz"}</div>
                        </div>
                        {component.foerderung.mitIsfp !== "-" && (
                          <div className="text-center">
                            <div className="text-2xl font-bold text-primary">{component.foerderung.mitIsfp}</div>
                            <div className="text-xs text-muted-foreground">mit iSFP</div>
                          </div>
                        )}
                        <div className="text-center">
                          <div className="text-2xl font-bold text-foreground">{component.foerderung.maxKosten} {component.foerderung.maxKosten !== "-" && "EUR"}</div>
