                      </div>
                      <p className="text-sm text-muted-foreground">
                        {component.foerderung.hinweis}
                      </p>
                    </div>
                  )}

                  {/* Subcomponents */}
                  <div className="space-y-6">
                    {component.subComponents.map((sub, subIndex) => (
                      <Card key={subIndex} className="overflow-hidden">
                        <CardHeader className="bg-muted/30 pb-4">
                          <CardTitle className="font-heading text-xl">{sub.name}</CardTitle>
                          <p className="text-muted-foreground">{sub.what}</p>
                        </CardHeader>
                        <CardContent className="pt-6 space-y-6">
                          {/* Materials */}
                          <div>
                            <h4 className="font-heading font-semibold mb-3 flex items-center gap-2">
                              <Layers className="w-5 h-5 text-primary" />
                              Materialien & Ausführungen
                            </h4>
                            <div className="flex flex-wrap gap-2">
                              {sub.materials.map((material, i) => (
                                <Badge key={i} variant="secondary">{material}</Badge>
                              ))}
                            </div>
                          </div>

                          {/* Variants */}
                          <div>
                            <h4 className="font-heading font-semibold mb-3 flex items-center gap-2">
                              <Ruler className="w-5 h-5 text-primary" />
                              Varianten
                            </h4>
                            <div className="grid sm:grid-cols-2 gap-3">
                              {sub.variants.map((variant, i) => (
                                <div key={i} className="p-3 rounded-lg bg-muted/30">
                                  <div className="font-medium mb-1">{variant.name}</div>
                                  <div className="text-sm text-muted-foreground">{variant.description}</div>
                                </div>
                              ))}
                            </div>
                          </div>

                          {/* Considerations */}
                          <div>
                            <h4 className="font-heading font-semibold mb-3 flex items-center gap-2">
                              <AlertTriangle className="w-5 h-5 text-primary" />
                              Wichtige Hinweise
                            </h4>
                            <ul className="space-y-2">
                              {sub.considerations.map((consideration, i) => (
                                <li key={i} className="flex items-start gap-2 text-sm">
                                  <CheckCircle2 className="w-4 h-4 text-primary flex-shrink-0 mt-0.5" />
                                  <span className="text-muted-foreground">{consideration}</span>
                                </li>
                              ))}
                            </ul>
                          </div>
                        </CardContent>
                      </Card>
                    ))}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </section>

        {/* CTA */}
        <section className="py-20 sm:py-32 bg-gradient-to-r from-emerald-800 via-primary to-teal-600 text-white">
          <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
            <h2 className="font-heading text-3xl sm:text-5xl font-bold mb-8">
              Welche Bauteile sollten Sie bei Ihrem Haus optimieren?
            </h2>
            <p className="text-xl sm:text-2xl text-white/90 mb-10 max-w-2xl mx-auto leading-relaxed">
              Mit einem individuellen Sanierungsfahrplan (iSFP) analysieren wir alle Bauteile 
              Ihres Hauses und zeigen Ihnen die wirtschaftlichsten Verbesserungsmöglichkeiten.
            </p>
            <div className="flex flex-col sm:flex-row gap-5 justify-center">
              <Button asChild size="lg" variant="secondary" className="text-primary text-lg px-8 py-6 shadow-lg">
                <Link href="/energieberatung/isfp" data-testid="link-isfp">
                  Mehr zum iSFP
                </Link>
              </Button>
              <Button asChild size="lg" variant="outline" className="border-2 border-white text-white text-lg px-8 py-6 hover:bg-white/10">
                <Link href="/anfrage" data-testid="link-booking">
                  Beratung anfragen
                </Link>
              </Button>
            </div>
          </div>
        </section>
      </div>
    </>
  );
}

