import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent } from "@/components/ui/card";
import { Mail, Send, CheckCircle, Loader2, Lock, ArrowRight } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface CalculatorResult {
  label: string;
  value: string;
}

interface EmailGateProps {
  calculatorType: string;
  calculatorName: string;
  results: CalculatorResult[];
  inputs: CalculatorResult[];
  children: React.ReactNode;
}

export function EmailGate({ 
  calculatorType, 
  calculatorName, 
  results, 
  inputs, 
  children 
}: EmailGateProps) {
  const [email, setEmail] = useState("");
  const [name, setName] = useState("");
  const [isUnlocked, setIsUnlocked] = useState(false);
  const [isSending, setIsSending] = useState(false);
  const [emailSent, setEmailSent] = useState(false);
  const { toast } = useToast();

  const isValidEmail = (email: string) => {
    return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!isValidEmail(email)) {
      toast({
        title: "Ungültige E-Mail",
        description: "Bitte geben Sie eine gültige E-Mail-Adresse ein.",
        variant: "destructive",
      });
      return;
    }

    setIsSending(true);

    try {
      await apiRequest("POST", "/api/calculator/send-results", {
        email,
        name: name || undefined,
        calculatorType,
        results,
        inputs,
      });

      setEmailSent(true);
      setIsUnlocked(true);

      toast({
        title: "Ergebnisse gesendet",
        description: "Die Berechnungsergebnisse wurden an Ihre E-Mail-Adresse gesendet.",
      });
    } catch (error) {
      console.error("Error sending results:", error);
      toast({
        title: "Fehler",
        description: "Die E-Mail konnte nicht gesendet werden. Bitte versuchen Sie es erneut.",
        variant: "destructive",
      });
    } finally {
      setIsSending(false);
    }
  };

  if (isUnlocked) {
    return (
      <div className="space-y-4">
        {emailSent && (
          <div className="flex items-center gap-3 p-4 bg-primary/10 border border-primary/20 rounded-lg mb-4">
            <CheckCircle className="w-5 h-5 text-primary flex-shrink-0" />
            <p className="text-sm text-foreground">
              Ergebnisse wurden an <strong>{email}</strong> gesendet.
            </p>
          </div>
        )}
        {children}
      </div>
    );
  }

  return (
    <Card className="bg-gradient-to-br from-primary/5 to-primary/10 border-primary/20">
      <CardContent className="p-6 sm:p-8">
        <div className="text-center mb-6">
          <div className="inline-flex items-center justify-center w-16 h-16 bg-primary/10 rounded-full mb-4">
            <Lock className="w-8 h-8 text-primary" />
          </div>
          <h3 className="text-xl font-semibold text-foreground mb-2">
            Ergebnisse freischalten
          </h3>
          <p className="text-muted-foreground text-sm max-w-md mx-auto">
            Geben Sie Ihre E-Mail-Adresse ein, um Ihre {calculatorName}-Ergebnisse zu sehen. 
            Wir senden Ihnen die Berechnung auch als PDF-Zusammenfassung.
          </p>
        </div>

        <form onSubmit={handleSubmit} className="max-w-sm mx-auto space-y-4">
          <div>
            <Label htmlFor="gate-name" className="text-sm">Name (optional)</Label>
            <Input
              id="gate-name"
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="Max Mustermann"
              className="mt-1"
              data-testid="input-gate-name"
            />
          </div>

          <div>
            <Label htmlFor="gate-email" className="text-sm">E-Mail-Adresse *</Label>
            <div className="relative mt-1">
              <Mail className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground" />
              <Input
                id="gate-email"
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="ihre@email.de"
                className="pl-10"
                required
                data-testid="input-gate-email"
              />
            </div>
          </div>

          <Button 
            type="submit" 
            className="w-full" 
            size="lg"
            disabled={isSending || !email}
            data-testid="button-unlock-results"
          >
            {isSending ? (
              <>
                <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                Wird gesendet...
              </>
            ) : (
              <>
                Ergebnisse anzeigen
                <ArrowRight className="w-4 h-4 ml-2" />
              </>
            )}
          </Button>

          <p className="text-xs text-muted-foreground text-center">
            Mit dem Absenden stimmen Sie zu, dass wir Ihnen die Ergebnisse per E-Mail zusenden. 
            Ihre Daten werden gemäß unserer Datenschutzerklärung verarbeitet.
          </p>
        </form>

        <div className="mt-8 pt-6 border-t border-border/50">
          <p className="text-xs text-muted-foreground text-center mb-3">
            Was Sie erhalten:
          </p>
          <div className="grid grid-cols-3 gap-4 text-center">
            <div>
              <div className="w-10 h-10 bg-primary/10 rounded-full flex items-center justify-center mx-auto mb-2">
                <CheckCircle className="w-5 h-5 text-primary" />
              </div>
              <p className="text-xs text-muted-foreground">Sofortige Ergebnisanzeige</p>
            </div>
            <div>
              <div className="w-10 h-10 bg-primary/10 rounded-full flex items-center justify-center mx-auto mb-2">
                <Mail className="w-5 h-5 text-primary" />
              </div>
              <p className="text-xs text-muted-foreground">E-Mail-Zusammenfassung</p>
            </div>
            <div>
              <div className="w-10 h-10 bg-primary/10 rounded-full flex items-center justify-center mx-auto mb-2">
                <Send className="w-5 h-5 text-primary" />
              </div>
              <p className="text-xs text-muted-foreground">Fördertipps inklusive</p>
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

