import { Info } from "lucide-react";

interface LegalDisclaimerProps {
  variant?: "voranalyse" | "foerderung";
  className?: string;
}

export function LegalDisclaimer({ variant = "foerderung", className = "" }: LegalDisclaimerProps) {
  const messages = {
    voranalyse: "Diese Voranalyse ersetzt nicht die offizielle Energieberatung und ist nicht förderfähig über BAFA/KfW.",
    foerderung: "Alle Förderangaben ohne Gewähr. Förderprogramme und -bedingungen können sich ändern."
  };

  return (
    <div className={`flex items-start gap-2 p-3 rounded-md bg-muted/50 border border-border text-sm text-muted-foreground ${className}`}>
      <Info className="h-4 w-4 mt-0.5 flex-shrink-0" />
      <span>{messages[variant]}</span>
    </div>
  );
}

client/src/pages/wissen/BauLexikon.tsx.content:
import { useState } from "react";
import { Link } from "wouter";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { 
  ArrowLeft, 
  Search, 
  BookOpen,
  TrendingUp,
  Zap,
  Wrench,
  FileText,
  Scale,
  Building2,
  ChevronDown,
  ChevronUp
} from "lucide-react";
import { FadeIn, FadeInView } from "@/components/motion";
import { SEO, createBreadcrumbSchema } from "@/components/SEO";

interface LexikonEintrag {
  begriff: string;
  definition: string;
}

interface Kategorie {
  id: string;
  title: string;
  icon: React.ElementType;
  eintraege: LexikonEintrag[];
}

export default function BauLexikon() {
  const [searchTerm, setSearchTerm] = useState("");
  const [expandedCategories, setExpandedCategories] = useState<string[]>(["wirtschaftlichkeit", "energie", "sanierung", "foerderung", "allgemein", "property"]);
  const [expandedTerms, setExpandedTerms] = useState<string[]>([]);

  const toggleTerm = (termId: string) => {
    setExpandedTerms(prev => 
      prev.includes(termId) 
        ? prev.filter(id => id !== termId)
        : [...prev, termId]
    );
  };

  const kategorien: Kategorie[] = [
    {
      id: "wirtschaftlichkeit",
      title: "Wirtschaftlichkeit & Kennzahlen",
      icon: TrendingUp,
      eintraege: [
        {
          begriff: "Kalkulationszins / Opportunitätskosten",
          definition: "Der Zinssatz, der bei Wirtschaftlichkeitsberechnungen verwendet wird, um zukünftige Zahlungen auf den heutigen Wert abzuzinsen. Er repräsentiert die entgangene Rendite einer alternativen Anlage (z.B. Festgeld) und liegt typischerweise zwischen 2-4%."
        },
        {
          begriff: "Kapitalwert (NPV - Net Present Value)",
          definition: "Die Summe aller auf den Investitionszeitpunkt abgezinsten Ein- und Auszahlungen einer Investition. Ein positiver Kapitalwert bedeutet, dass die Investition wirtschaftlich sinnvoll ist und mehr erwirtschaftet als die alternative Geldanlage."
        },
        {
          begriff: "Return on Investment (ROI)",
          definition: "Kennzahl zur Messung der Rentabilität einer Investition. Der ROI gibt an, wie viel Prozent des eingesetzten Kapitals als Gewinn zurückfließt. Berechnung: (Gewinn / Investition) × 100%."
        },
        {
          begriff: "Amortisationsdauer",
          definition: "Die Zeit, bis sich eine Investition durch Einsparungen oder Einnahmen selbst refinanziert hat. Bei Sanierungsmaßnahmen typischerweise 8-20 Jahre, abhängig von Maßnahme und Förderung."
        },
        {
          begriff: "Nettokosten nach Förderung",
          definition: "Die tatsächlichen Kosten einer Maßnahme nach Abzug aller Fördermittel (BAFA-Zuschüsse, KfW-Tilgungszuschüsse). Diese Kennzahl ist entscheidend für die Wirtschaftlichkeitsbetrachtung."
        },
        {
          begriff: "Jährliche Energiepreissteigerung",
          definition: "Die angenommene prozentuale Steigerung der Energiepreise pro Jahr für Wirtschaftlichkeitsberechnungen. Historisch liegt diese bei 3-5% jährlich für fossile Energieträger."
        },
        {
          begriff: "A/V-Verhältnis",
          definition: "Das Verhältnis von wärmeübertragender Außenfläche (A) zum beheizten Gebäudevolumen (V). Je kleiner der Wert, desto kompakter und energetisch günstiger ist das Gebäude. Typische Werte: EFH 0,7-1,0, MFH 0,3-0,5."
        },
        {
          begriff: "LCC (Life Cycle Costing)",
          definition: "Lebenszykluskostenrechnung - betrachtet alle Kosten einer Anlage über ihre gesamte Nutzungsdauer: Anschaffung, Betrieb, Wartung, Reparatur und Entsorgung. Ermöglicht faire Vergleiche verschiedener Systeme."
        },
        {
          begriff: "Contracting (Energie)",
          definition: "Ein Finanzierungsmodell, bei dem ein Dienstleister die Energieversorgung übernimmt. Er investiert in die Anlage (z.B. Heizung, PV) und der Gebäudeeigentümer zahlt für die gelieferte Energie. Vorteil: Keine Anfangsinvestition."
        },
        {
          begriff: "COP (Coefficient of Performance)",
          definition: "Leistungszahl einer Wärmepumpe zu einem bestimmten Zeitpunkt. Gibt an, wie viel Wärmeenergie pro eingesetzter elektrischer Energie erzeugt wird. Beispiel: COP 4 bedeutet, aus 1 kWh Strom werden 4 kWh Wärme."
        },
        {
          begriff: "JAZ (Jahresarbeitszahl)",
          definition: "Die über ein Jahr gemittelte Effizienz einer Wärmepumpe unter realen Betriebsbedingungen. Die JAZ berücksichtigt alle Betriebszustände und ist aussagekräftiger als der COP. Gute Wärmepumpen erreichen JAZ > 3,5."
        }
      ]
    },
    {
      id: "energie",
      title: "Energie & Technik",
      icon: Zap,
      eintraege: [
        {
          begriff: "U-Wert (Wärmedurchgangskoeffizient)",
          definition: "Maß für den Wärmeverlust durch ein Bauteil in W/(m²·K). Je niedriger der U-Wert, desto besser die Dämmwirkung. Beispiele: Altbau-Fenster ca. 2,8, modernes Dreifachglas ca. 0,6, gedämmte Wand ca. 0,15-0,24."
        },
        {
          begriff: "Primärenergiebedarf (Qp)",
          definition: "Der gesamte Energiebedarf eines Gebäudes inklusive der Energie für Gewinnung, Umwandlung und Transport des Energieträgers. Erneuerbare Energien haben einen niedrigen Primärenergiefaktor (z.B. Strom aus PV: 0,0)."
        },
        {
          begriff: "Endenergiebedarf",
          definition: "Die Energiemenge, die dem Gebäude tatsächlich zugeführt werden muss (z.B. Gas in m³, Strom in kWh). Dieser Wert ist relevant für die Energiekosten und steht auf dem Energieausweis."
        },
        {
          begriff: "Transmissionswärmeverlust (H'T)",
          definition: "Kennwert für die Wärmeverluste durch die Gebäudehülle in W/(m²·K). Ein niedriger H'T-Wert zeigt eine gut gedämmte Gebäudehülle an. Anforderung KfW 55: max. 0,70 × Referenzwert."
        },
        {
          begriff: "Wärmebrücke",
          definition: "Bereiche in der Gebäudehülle mit erhöhtem Wärmeverlust, z.B. Balkone, Fensterstürze, Ecken. Wärmebrücken können zu Schimmelbildung führen und müssen bei Sanierungen minimiert werden."
        },
        {
          begriff: "Luftdichtheit",
          definition: "Maß für die Dichtigkeit der Gebäudehülle gegen unkontrollierten Luftaustausch. Gemessen mit dem Blower-Door-Test, angegeben als n50-Wert (Luftwechselrate bei 50 Pascal Druckdifferenz)."
        },
        {
          begriff: "Heizkurve (Heizkennlinie)",
          definition: "Die Einstellung, die festlegt, welche Vorlauftemperatur bei welcher Außentemperatur bereitgestellt wird. Eine flache, niedrige Heizkurve ist effizient, setzt aber gute Dämmung und große Heizflächen voraus."
        },
        {
          begriff: "Hydraulischer Abgleich",
          definition: "Einstellung der Heizungsanlage, sodass jeder Heizkörper genau die benötigte Wassermenge erhält. Pflicht bei gefördertem Heizungstausch. Spart 5-15% Heizenergie und erhöht den Komfort."
        },
        {
          begriff: "Blower-Door-Test",
          definition: "Messverfahren zur Bestimmung der Luftdichtheit eines Gebäudes. Ein Ventilator erzeugt Unter-/Überdruck, die Leckagestellen werden identifiziert. Pflicht für KfW 55/40, empfohlen bei jeder Sanierung."
        },
        {
          begriff: "Heizlastberechnung",
          definition: "Berechnung der benötigten Heizleistung nach DIN EN 12831, basierend auf Gebäudehülle, Wärmeverlusten und Normauslegungstemperatur. Grundlage für die korrekte Dimensionierung der Heizungsanlage."
        },
        {
          begriff: "Wärmepumpe",
          definition: "Heizsystem, das Umweltwärme (Luft, Erde, Wasser) mittels Strom auf nutzbares Temperaturniveau anhebt. Effizient bei Niedertemperatursystemen (Fußbodenheizung) und gut gedämmten Gebäuden. Erfüllt die 65%-EE-Anforderung."
        },
        {
          begriff: "Vorlauftemperatur",
          definition: "Die Temperatur des Heizwassers, das vom Wärmeerzeuger zu den Heizkörpern fließt. Niedrige Vorlauftemperaturen (35-45°C) sind Voraussetzung für effiziente Wärmepumpenbetrieb."
        },
        {
          begriff: "Heizwert",
          definition: "Der Energiegehalt eines Brennstoffs. Wichtig für Wirtschaftlichkeitsberechnungen: 1 Liter Heizöl entspricht ca. 10 kWh, 1 m³ Erdgas ebenfalls ca. 10 kWh. Der Brennwert (Kondensation einbezogen) liegt etwa 10% höher."
        }
      ]
    },
    {
      id: "sanierung",
      title: "Sanierung & Bau",
      icon: Wrench,
      eintraege: [
        {
          begriff: "Solarthermie",
          definition: "System zur Nutzung von Sonnenwärme für Warmwasser und/oder Heizungsunterstützung. Flach- oder Vakuumröhrenkollektoren erwärmen eine Solarflüssigkeit, die Wärme an einen Speicher überträgt. Förderfähig mit 25% über BEG EM."
        },
        {
          begriff: "Einzelmaßnahmen (BEG EM)",
          definition: "Bundesförderung für einzelne energetische Sanierungsmaßnahmen wie Dämmung, Fenstertausch, Heizungstausch oder Anlagentechnik. Förderung über BAFA (Zuschuss) oder KfW (Kredit). Nicht für Komplettsanierungen zum Effizienzhaus."
        },
        {
          begriff: "Gesamtsanierung / Effizienzhaus",
          definition: "Umfassende energetische Sanierung eines Gebäudes auf einen KfW-Effizienzhaus-Standard (z.B. EH 85, 70, 55, 40). Erfordert koordinierte Maßnahmen an Hülle und Technik. Höhere Förderung als Einzelmaßnahmen, aber komplexer."
        },
        {
          begriff: "Nachhaltige Baustoffe",
          definition: "Materialien mit geringem ökologischen Fußabdruck, z.B. Holzfaserdämmung, Zellulose, Hanf, Lehm. Sie bieten oft guten sommerlichen Wärmeschutz und verbessern das Raumklima. Teilweise höhere Kosten, aber bessere Ökobilanz."
        },
        {
          begriff: "Lüftungskonzept",
          definition: "Planung der kontrollierten Wohnraumlüftung nach DIN 1946-6. Pflicht bei Fenstertausch oder Dachsanierung in gedämmten Gebäuden. Unterscheidet zwischen Fensterlüftung, Abluftsystem und Lüftung mit Wärmerückgewinnung."
        },
        {
          begriff: "Individueller Sanierungsfahrplan (iSFP)",
          definition: "Detaillierter, durch einen Energieberater erstellter Stufenplan für die energetische Sanierung eines Gebäudes. Der iSFP berechtigt zum iSFP-Bonus (+5% auf Einzelmaßnahmen) und wird zu 80% gefördert (max. 1.300€)."
        },
        {
          begriff: "Wärmedämm-Verbundsystem (WDVS)",
          definition: "Außendämmung bestehend aus Dämmplatten (EPS, Mineralwolle), Armierungsschicht und Putz. Das am häufigsten verwendete System zur Fassadendämmung. Typische Dämmstärken: 14-20 cm."
        },
        {
          begriff: "Energiemanagement-System (EMS)",
          definition: "Intelligentes Steuerungssystem zur Optimierung des Energieverbrauchs. Koordiniert PV-Anlage, Speicher, Wärmepumpe und Wallbox für maximalen Eigenverbrauch. Förderfähig als 'Anlagentechnik' über BEG EM."
        },
        {
          begriff: "Wärmerückgewinnung (WRG)",
          definition: "Technologie zur Rückgewinnung von Wärme aus der Abluft bei kontrollierten Lüftungsanlagen. Wärmetauscher übertragen 80-95% der Wärme auf die frische Zuluft. Reduziert Lüftungswärmeverluste erheblich und erhöht den Wohnkomfort."
        }
      ]
    },
    {
      id: "foerderung",
      title: "Förderprogramme & Nachweise",
      icon: FileText,
      eintraege: [
        {
          begriff: "KfW-Effizienzhaus",
          definition: "Von der KfW definierter technischer Standard für energieeffiziente Gebäude. Die Zahl gibt den max. Primärenergiebedarf in % eines Referenzgebäudes an. Standards: EH 85, 70, 55, 40 (plus EE-Klasse für 65% Erneuerbare)."
        },
        {
          begriff: "BAFA / KfW / BEG",
          definition: "BAFA: Bundesamt für Wirtschaft und Ausfuhrkontrolle, zuständig für Einzelmaßnahmen-Zuschüsse. KfW: Kreditanstalt für Wiederaufbau, bietet zinsgünstige Kredite. BEG: Bundesförderung für effiziente Gebäude, der Rahmen für alle Förderprogramme."
        },
        {
          begriff: "Förderquote / iSFP-Bonus",
          definition: "Die Förderquote gibt den Prozentsatz der förderfähigen Kosten an, die als Zuschuss gewährt werden. Der iSFP-Bonus (+5%) erhöht diese Quote, wenn Maßnahmen aus einem individuellen Sanierungsfahrplan umgesetzt werden."
        },
        {
          begriff: "Verwendungsnachweis / BnD",
          definition: "Bestätigung nach Durchführung (BnD) - der Nachweis, dass eine geförderte Maßnahme ordnungsgemäß umgesetzt wurde. Muss vom Energieberater und Fachunternehmen bestätigt werden, um die Förderung zu erhalten."
        },
        {
          begriff: "Zuwendungsbescheid",
          definition: "Offizielle Förderzusage von BAFA oder KfW, die Förderhöhe und Bedingungen festlegt. Erst nach Erhalt des Zuwendungsbescheids darf mit der Maßnahme begonnen werden (bei BAFA) oder der Kredit ausgezahlt werden (bei KfW)."
        },
        {
          begriff: "Förderfähigkeit",
          definition: "Voraussetzungen, die ein Gebäude oder eine Maßnahme erfüllen muss, um Fördermittel zu erhalten. Dazu gehören: Gebäudealter, technische Mindestanforderungen (z.B. U-Werte), Einschaltung eines Energieberaters."
        },
        {
          begriff: "Energieausweis",
          definition: "Dokument, das die energetische Qualität eines Gebäudes ausweist. Es gibt Bedarfsausweise (auf Basis von Berechnungen) und Verbrauchsausweise (auf Basis realer Verbräuche). Pflicht bei Verkauf, Vermietung und Neubau."
        }
      ]
    },
    {
      id: "allgemein",
      title: "Allgemein & Rechtlich",
      icon: Scale,
      eintraege: [
        {
          begriff: "GEG (Gebäudeenergiegesetz)",
          definition: "Das zentrale Gesetz für Energieeffizienz in Gebäuden in Deutschland seit 2020. Regelt Anforderungen an Neubauten, Sanierungen und Heizungsanlagen. Seit 2024 mit der 65%-Erneuerbare-Energien-Regel für neue Heizungen."
        },
        {
          begriff: "WEMoG (Wohnungseigentumsmodernisierungsgesetz)",
          definition: "Reform des Wohnungseigentumsgesetzes von 2020. Erleichtert energetische Sanierungen in WEG-Anlagen: Beschlüsse mit einfacher Mehrheit möglich, privilegierte Maßnahmen für E-Mobilität und barrierefreies Wohnen."
        },
        {
          begriff: "EnSimiMaV",
          definition: "Verordnung zur Sicherung der Energieversorgung über mittelfristig wirksame Maßnahmen. Verpflichtet zu Heizungsprüfungen, hydraulischem Abgleich (bei Gaszentralheizungen >9 WE) und Optimierung von Wärmeerzeugern."
        },
        {
          begriff: "Modernisierungsumlage",
          definition: "Vermieter können 8% der Modernisierungskosten jährlich auf die Miete umlegen. Bei energetischen Maßnahmen ist die Erhöhung auf max. 50 Cent/m² in 6 Jahren begrenzt. Fördermittel müssen abgezogen werden."
        },
        {
          begriff: "Dämmstärke",
