            <div className="grid grid-cols-2 md:grid-cols-3 gap-3 sm:gap-4">
              {filteredKategorien.map((kategorie) => {
                const Icon = kategorie.icon;
                const isExpanded = expandedCategories.includes(kategorie.id);

                return (
                  <button
                    key={kategorie.id}
                    onClick={() => toggleCategory(kategorie.id)}
                    className={`bg-surface rounded-xl p-4 text-left hover-elevate transition-colors ${isExpanded ? 'ring-2 ring-primary' : ''}`}
                    data-testid={`button-toggle-${kategorie.id}`}
                  >
                    <div className="flex items-center gap-3">
                      <div className="w-10 h-10 bg-primary/10 rounded-lg flex items-center justify-center flex-shrink-0">
                        <Icon className="w-5 h-5 text-primary" />
                      </div>
                      <div>
                        <h2 className="font-bold text-foreground font-heading text-sm sm:text-base">{kategorie.title}</h2>
                        <p className="text-xs text-muted-foreground">{kategorie.eintraege.length} Begriffe</p>
                      </div>
                    </div>
                  </button>
                );
              })}
            </div>

            {filteredKategorien.map((kategorie) => {
              const isExpanded = expandedCategories.includes(kategorie.id);
              if (!isExpanded) return null;

              return (
                <FadeInView key={kategorie.id} className="mt-6">
                  <div className="bg-surface rounded-xl p-4 sm:p-6" data-testid={`category-${kategorie.id}`}>
                    <div className="flex items-center justify-between mb-4">
                      <h3 className="font-bold text-foreground font-heading">{kategorie.title}</h3>
                      <button
                        onClick={() => toggleCategory(kategorie.id)}
                        className="text-muted-foreground hover:text-foreground"
                        data-testid={`button-close-${kategorie.id}`}
                      >
                        <ChevronUp className="w-5 h-5" />
                      </button>
                    </div>
                    <div className="space-y-2">
                      {kategorie.eintraege.map((eintrag, index) => {
                        const termId = `${kategorie.id}-${index}`;
                        const isTermExpanded = expandedTerms.includes(termId);

                        return (
                          <div key={index} className="bg-background rounded-lg border border-border overflow-hidden" data-testid={`term-${kategorie.id}-${index}`}>
                            <button
                              onClick={() => toggleTerm(termId)}
                              className="w-full flex items-center justify-between p-3 sm:p-4 text-left hover-elevate"
                              data-testid={`button-term-${termId}`}
                            >
                              <h3 className="font-semibold text-foreground font-heading text-sm">
                                {eintrag.begriff}
                              </h3>
                              {isTermExpanded ? (
                                <ChevronUp className="w-4 h-4 text-muted-foreground flex-shrink-0 ml-2" />
                              ) : (
                                <ChevronDown className="w-4 h-4 text-muted-foreground flex-shrink-0 ml-2" />
                              )}
                            </button>
                            {isTermExpanded && (
                              <div className="px-3 sm:px-4 pb-3 sm:pb-4 pt-0">
                                <p className="text-sm text-muted-foreground leading-relaxed">
                                  {eintrag.definition}
                                </p>
                              </div>
                            )}
                          </div>
                        );
                      })}
                    </div>
                  </div>
                </FadeInView>
              );
            })}

            {filteredKategorien.length === 0 && searchTerm && (
              <FadeInView>
                <div className="text-center py-12">
                  <BookOpen className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
                  <h3 className="text-lg font-semibold text-foreground mb-2">Kein Begriff gefunden</h3>
                  <p className="text-muted-foreground">
                    Versuchen Sie einen anderen Suchbegriff oder{" "}
                    <button 
                      onClick={() => setSearchTerm("")}
                      className="text-primary hover:underline"
                    >
                      zeigen Sie alle Begriffe
                    </button>
                  </p>
                </div>
              </FadeInView>
            )}

            <FadeInView className="mt-12">
              <div className="bg-primary/5 dark:bg-primary/10 rounded-xl p-6 text-center">
                <h3 className="font-bold text-foreground mb-2 font-heading">Begriff vermisst?</h3>
                <p className="text-sm text-muted-foreground mb-4">
                  Kontaktieren Sie uns, wenn Sie einen Begriff nicht finden oder eine genauere Erklärung benötigen.
                </p>
                <Link href="/kontakt">
                  <Button data-testid="button-contact">
                    Kontakt aufnehmen
                  </Button>
                </Link>
              </div>
            </FadeInView>
          </div>
        </section>
      </div>
    </>
  );
}

