// HubSpot Integration - Replit Connector
import { Client } from '@hubspot/api-client';

let connectionSettings: any;

// Retry helper with exponential backoff
async function withRetry<T>(
  operation: () => Promise<T>,
  maxRetries: number = 3,
  baseDelay: number = 1000
): Promise<T> {
  let lastError: Error | null = null;

  for (let attempt = 1; attempt <= maxRetries; attempt++) {
    try {
      return await operation();
    } catch (error: any) {
      lastError = error;

      // Don't retry on 409 (conflict/duplicate) - that's expected
      if (error?.code === 409) {
        throw error;
      }

      // Don't retry on 400 (bad request) - that's a client error
      if (error?.response?.status === 400) {
        throw error;
      }

      if (attempt < maxRetries) {
        const delay = baseDelay * Math.pow(2, attempt - 1);
        console.log(`HubSpot retry attempt ${attempt}/${maxRetries} after ${delay}ms`);
        await new Promise(resolve => setTimeout(resolve, delay));
      }
    }
  }

  console.error(`HubSpot operation failed after ${maxRetries} attempts`);
  throw lastError;
}

async function getAccessToken() {
  if (connectionSettings && connectionSettings.settings.expires_at && new Date(connectionSettings.settings.expires_at).getTime() > Date.now()) {
    return connectionSettings.settings.access_token;
  }

  const hostname = process.env.REPLIT_CONNECTORS_HOSTNAME;
  const xReplitToken = process.env.REPL_IDENTITY 
    ? 'repl ' + process.env.REPL_IDENTITY 
    : process.env.WEB_REPL_RENEWAL 
    ? 'depl ' + process.env.WEB_REPL_RENEWAL 
    : null;

  if (!xReplitToken) {
    throw new Error('X_REPLIT_TOKEN not found for repl/depl');
  }

  connectionSettings = await fetch(
    'https://' + hostname + '/api/v2/connection?include_secrets=true&connector_names=hubspot',
    {
      headers: {
        'Accept': 'application/json',
        'X_REPLIT_TOKEN': xReplitToken
      }
    }
  ).then(res => res.json()).then(data => data.items?.[0]);

  const accessToken = connectionSettings?.settings?.access_token || connectionSettings?.settings?.oauth?.credentials?.access_token;

  if (!connectionSettings || !accessToken) {
    throw new Error('HubSpot not connected');
  }
  return accessToken;
}

async function getUncachableHubSpotClient() {
  const accessToken = await getAccessToken();
  return new Client({ accessToken });
}

// Form types for HubSpot workflow triggers
export type FormType = 'booking' | 'calculator' | 'partner' | 'career';

// Create a contact in HubSpot from a booking request
// Note: Custom properties (palvar_form_type, palvar_service_type, palvar_lead_source) 
// must be created in HubSpot for workflow triggers to work
export async function createBookingContact(data: {
  name: string;
  email: string;
  phone?: string;
  service: string;
  message?: string;
  packageType?: string;
  buildingType?: string;
}) {
  return withRetry(async () => {
    try {
      const hubspot = await getUncachableHubSpotClient();

      const nameParts = data.name.split(' ');
      const firstName = nameParts[0] || '';
      const lastName = nameParts.slice(1).join(' ') || '';

      const properties: Record<string, string> = {
        firstname: firstName,
        lastname: lastName,
        email: data.email,
        phone: data.phone || '',
        hs_lead_status: 'NEW',
        lifecyclestage: 'lead',
      };

      const notes = [
        `Anfrage: ${data.service}`,
        data.packageType ? `Paket: ${data.packageType}` : '',
        data.buildingType ? `Gebäudetyp: ${data.buildingType}` : '',
        data.message ? `Nachricht: ${data.message}` : '',
      ].filter(Boolean).join('\n');

      const contact = await hubspot.crm.contacts.basicApi.create({
        properties,
        associations: []
      });

      console.log('HubSpot contact created for booking:', contact.id);

      // Set booking_confirmed = true for workflow trigger
      // Note: Custom property 'booking_confirmed' must be created in HubSpot as a single-line text or boolean
      try {
        await hubspot.crm.contacts.basicApi.update(contact.id, {
          properties: {
            booking_type: data.service,
            booking_confirmed: 'true',
          }
        });
        console.log('HubSpot booking_confirmed set to true for workflow trigger');
      } catch (updateError) {
        console.log('Custom properties update skipped - create booking_type and booking_confirmed in HubSpot');
      }

      try {
        await hubspot.crm.objects.notes.basicApi.create({
          properties: {
            hs_note_body: notes,
            hs_timestamp: new Date().toISOString(),
          },
          associations: [{
            to: { id: contact.id },
            types: [{ associationCategory: 'HUBSPOT_DEFINED', associationTypeId: 202 }]
          }]
        });
      } catch (noteError) {
        console.log('Note creation skipped (optional)');
      }

      return contact;
    } catch (error: any) {
      if (error?.code === 409) {
        console.log('HubSpot contact already exists for:', data.email);
        return null;
      }
      throw error;
    }
  });
}

// Create a contact in HubSpot from calculator submission
