// Create a contact in HubSpot from a partner registration
export async function createPartnerContact(data: {
  companyName: string;
  contactPerson: string;
  email: string;
  phone: string;
  address: string;
  website?: string;
  trades: string[];
}) {
  return withRetry(async () => {
    try {
      const hubspot = await getUncachableHubSpotClient();

      const nameParts = data.contactPerson.split(' ');
      const firstName = nameParts[0] || '';
      const lastName = nameParts.slice(1).join(' ') || '';

      const contact = await hubspot.crm.contacts.basicApi.create({
        properties: {
          firstname: firstName,
          lastname: lastName,
          email: data.email,
          phone: data.phone,
          company: data.companyName,
          website: data.website || '',
          address: data.address,
          hs_lead_status: 'NEW',
          lifecyclestage: 'lead',
        },
        associations: []
      });

      console.log('HubSpot partner contact created:', contact.id);

      // Set partner_confirmed = true for workflow trigger
      try {
        await hubspot.crm.contacts.basicApi.update(contact.id, {
          properties: {
            partner_trades: data.trades.join(', '),
            partner_confirmed: 'true',
          }
        });
        console.log('HubSpot partner_confirmed set to true for workflow trigger');
      } catch (updateError) {
        console.log('Custom properties update skipped');
      }

      try {
        const company = await hubspot.crm.companies.basicApi.create({
          properties: {
            name: data.companyName,
            phone: data.phone,
            website: data.website || '',
            address: data.address,
            description: `Handwerkspartner - Gewerke: ${data.trades.join(', ')}`,
          },
          associations: []
        });
        console.log('HubSpot company created:', company.id);
      } catch (companyError) {
        console.error('Error creating HubSpot company:', companyError);
      }

      return contact;
    } catch (error: any) {
      if (error?.code === 409) {
        console.log('HubSpot contact already exists for:', data.email);
        return null;
      }
      throw error;
    }
  });
}
